/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";
import "../Usuario/userProfile.css";
import { Row, Col } from "react-materialize";
import "./centerIfSmall.css";

class atividadeEventoItem extends Component {
  buildRow = (icon, key, value) => (
    <Row>
      <Col s={12} m={6}>
        <i className="material-icons left hide-on-small-and-down">{icon}</i>
        <b className="left hide-on-small-and-down">{key}</b>
        <b className="really-center-span hide-on-med-and-up">{key}</b>
      </Col>

      <Col s={12} m={6}>
        <span className="right hide-on-small-and-down">{value}</span>
        <span className="really-center-span hide-on-med-and-up">{value}</span>
      </Col>
    </Row>
  );
  render() {
    const { atividade } = this.props;
    const titleStyle = {
      fontWeight: "bold",
      fontSize: `${2}rem`,
      fontFamily: "Nunito"
    };
    return (
      <div className="row">
        <div className="col s12 ">
          <div className="card">
            <div className="card-content">
              <h5 className="card-title center" style={titleStyle}>
                {atividade.nome}
              </h5>
              <br />
              <div className="basicInfo grey-text text-darken-2">
                <p>
                  {atividade.descricao.length > 600
                    ? atividade.descricao.substring(0, 600) + "..."
                    : atividade.descricao}
                </p>
                <hr className="style-six" />
                {atividade.preRequisitos ? (
                  <Fragment>
                    {this.buildRow(
                      "school",
                      "Pré-requisitos:",
                      atividade.preRequisitos
                    )}
                    <hr className="style-six" />
                  </Fragment>
                ) : null}

                {this.buildRow(
                  "access_time",
                  "Carga horária:",
                  atividade.cargaHoraria
                )}
                <hr className="style-six" />
                {this.buildRow(
                  "timeline",
                  "Categoria:",
                  atividade.categoria.nome
                )}
              </div>
              <div className="card-action">
                <Link
                  to={`/pet/eventos/${atividade.eventoId}/atividades/${atividade.id}`}
                  className="btn-small blue lighten-1 z-depth-2 "
                  style={{ float: "right" }}
                >
                  Ver detalhes
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default atividadeEventoItem;
