import React, { Component, Fragment } from "react";
import Header from "../Header/Header";
import CardsPetianos from "./CardsPetianos";
import Axios from "axios";
import PathName from "../pathConst";
import { GridList, GridListTile } from "@material-ui/core";
import withWidth from "@material-ui/core/withWidth";
import compose from "recompose/compose";
import ReactLoading from "react-loading";

class Petianos extends Component {
  componentWillMount() {
    const filter = {
      order: {
        nome: "ASC"
      }
    };
    Axios.get(
      `http://${PathName}/pet/api/Usuarios/petianos?filter=${JSON.stringify(
        filter
      )}`
    ).then(res => this.setState({ petianos: res.data, done: true }));
  }

  state = {
    petianos: []
  };

  render() {
    const { petianos, done } = this.state;
    const { width } = this.props;
    const columns = width === "xs" || width === "sm" ? 1 : 3;
    if (!done) {
      return (
        <div style={styleLoading}>
          <ReactLoading type={"bars"} color={"#42a5f5"} />
        </div>
      );
    } else {
      return (
        <Fragment>
          <Header />
          <div className="container">
            <GridList cellHeight={"auto"} cols={columns}>
              {petianos.map((petiano, i) => (
                <GridListTile key={i}>
                  <CardsPetianos petiano={petiano} />
                </GridListTile>
              ))}
            </GridList>
          </div>
        </Fragment>
      );
    }
  }
}

const styleLoading = {
  position: "absolute",
  top: "50%",
  left: "50%",
  marginTop: "-50px",
  marginLeft: "-50px",
  width: "100px",
  height: "100px"
};

export default compose(withWidth())(Petianos);
