import React, { Component, Fragment } from "react";
import Header from "../Header/Header";
import Axios from "axios";
import PathName from "../pathConst";
import TabEvento from "./tabsOnGoingAndFinishedEvents";
import CardError from "../cardError";
import ShowEventosAtivos from "./showEventosAtivos";
import ShowEventosAnteriores from "./showEventosAnteriores";
import ReactLoading from "react-loading";

export class showEvento extends Component {
  constructor(props) {
    super(props);
    this.state = {
      eventosAtivos: [],
      eventosPassados: [],
      showEventosAtivos: true,
      showEventosAnteriores: false,
      doneAtuais: false,
      doneAntigos: false
    };
  }

  componentWillMount() {
    let date = new Date();
    date.setHours(0, 0, 0, 0);
    Axios.get(
      `http://${PathName}/pet/api/Eventos?filter={
      "where": { 
        "dataFim": {"gte": "${date.toISOString()}"}
      }
    }`
    ).then(res => {
      this.setState({ eventosAtivos: res.data, doneAtuais: true });
    });
    Axios.get(
      `http://${PathName}/pet/api/Eventos?filter={
      "where": { 
        "dataFim": {"lt": "${date.toISOString()}"}
      }
    }`
    ).then(res => {
      this.setState({ eventosPassados: res.data, doneAntigos: true });
    });
  }

  handleClickEventosAtivos = () => {
    this.setState({ showEventosAtivos: true, showEventosAnteriores: false });
  };

  handleClickEventosAnteriores = () => {
    this.setState({ showEventosAtivos: false, showEventosAnteriores: true });
  };

  render() {
    const {
      eventosAtivos,
      eventosPassados,
      showEventosAtivos,
      showEventosAnteriores,
      doneAtuais,
      doneAntigos
    } = this.state;
    if (!doneAtuais && !doneAntigos) {
      return (
        <div style={styleLoading}>
          <ReactLoading type={"bars"} color={"#42a5f5"} />
        </div>
      );
    } else if (showEventosAtivos) {
      if (eventosAtivos.length === 0) {
        return (
          <Fragment>
            <Header />
            <TabEvento
              handleClickEventosAtivos={this.handleClickEventosAtivos}
              handleClickEventosAnteriores={this.handleClickEventosAnteriores}
            />
            <CardError
              titulo={"Não consegui encontrar nenhum evento"}
              mensagem={
                "No momento, pode ser que estamos em manutenção. Volte mais tarde para conferir os eventos. "
              }
            />
          </Fragment>
        );
      } else {
        return (
          <Fragment>
            <Header />
            <TabEvento
              handleClickEventosAtivos={this.handleClickEventosAtivos}
              handleClickEventosAnteriores={this.handleClickEventosAnteriores}
            />
            <ShowEventosAtivos eventosAtivos={eventosAtivos} />
          </Fragment>
        );
      }
    } else if (showEventosAnteriores) {
      if (eventosPassados.length === 0) {
        return (
          <Fragment>
            <Header />
            <TabEvento
              handleClickEventosAtivos={this.handleClickEventosAtivos}
              handleClickEventosAnteriores={this.handleClickEventosAnteriores}
            />
            <CardError
              titulo={"Não consegui encontrar nenhum evento anterior"}
            />
          </Fragment>
        );
      } else {
        return (
          <Fragment>
            <Header />
            <TabEvento
              handleClickEventosAtivos={this.handleClickEventosAtivos}
              handleClickEventosAnteriores={this.handleClickEventosAnteriores}
            />
            <ShowEventosAnteriores eventosPassados={eventosPassados} />
          </Fragment>
        );
      }
    }
  }
}

const styleLoading = {
  position: "absolute",
  top: "50%",
  left: "50%",
  marginTop: "-50px",
  marginLeft: "-50px",
  width: "100px",
  height: "100px"
};
export default showEvento;
