import React, { Fragment } from 'react';
import {
  EditController,
  FormTab,
  TabbedForm,
  EditButton,
  TextInput,
  Edit,
  List,
  ShowButton,
  CardActions,
  SaveButton,
  Toolbar,
  BooleanInput,
  NumberInput,
  ReferenceManyField,
  Datagrid,
  TextField,
  DateField
} from 'react-admin';
import NovoDia from './addDia';
import DeleteButtonWithConfirmation from '../deleteButtonWithConfirmation';
import BackBotton from '../backButton';
import { required } from 'ra-core';

const PostActions = props => (
  <CardActions>
    <NovoDia {...props} />
  </CardActions>
);
const PostCreateToolbar = props => (
  <Toolbar {...props}>
    <SaveButton redirect={false} />
    <BackBotton />
  </Toolbar>
);

export const PostCreate = ({ permissions, ...props }) => (
  <EditController {...props}>
    {({ resource, record, redirect, save, basePath, version }) => {
      return (
        <TabbedForm
          basePath={basePath}
          redirect={redirect}
          resource={resource}
          record={record}
          save={save}
          version={version}
          toolbar={<PostCreateToolbar />}
        >
          <FormTab label="Dados da turma">
            <TextInput validate={required()} source="nome" label="Local" />
            {record && record.limited && (
              <NumberInput
                validate={required()}
                source="quantidadeMaxInscritos"
              />
            )}
            <BooleanInput
              validate={required()}
              source="limited"
              label="Possui limite de inscritos?"
            ></BooleanInput>
          </FormTab>
          <FormTab label="Dias">
            <Fragment>
              <Edit actions={null} title={'Turma/'} {...props}>
                <ReferenceManyField
                  {...props}
                  basePath={basePath}
                  resource={resource}
                  record={record}
                  reference="Dias"
                  target="turmaId"
                  title={null}
                >
                  <List
                    actions={<PostActions {...{ turmaId: props.id }} />}
                    title={null}
                    bulkActionButtons={false}
                    filter={{ turmaId: props.id }}
                    {...props}
                  >
                    <Datagrid>
                      <TextField source="cargaHoraria" />
                      <DateField showTime locales="pt-BR" source="dia" />
                      <ShowButton label="Visualizar presenÃ§a" />
                      <EditButton label="Gerenciar dados" />
                      {permissions && permissions === 'admin' && (
                        <DeleteButtonWithConfirmation
                          redirect={false}
                          label="Remover dia"
                          undoable={true}
                        />
                      )}
                    </Datagrid>
                  </List>
                </ReferenceManyField>
              </Edit>
            </Fragment>
          </FormTab>
        </TabbedForm>
      );
    }}
  </EditController>
);

export default PostCreate;
