import React, { Component } from 'react';
import formatDate from './formatDate';
import { Link } from 'react-router-dom';

class sobreEvento extends Component {
  componentWillMount() {
    const userId = localStorage.getItem('user_id');
    if (userId === null) {
      this.setState({ userLogged: false });
    } else {
      this.setState({ userLogged: true });
    }
  }
  state = {
    userLogged: false
  };

  getStyleButton = () => {
    const { userLogged } = this.state;
    return userLogged
      ? 'btn blue lighten-1 right'
      : 'disabled btn blue lighten-1 right';
  };

  getTextButton = () => {
    const { userLogged } = this.state;
    return userLogged ? 'Inscrever-se' : 'Efetue login p/ inscrever-se';
  };

  formatValor = valor => {
    return valor === 0 ? 'Gratuito' : `R$ ${valor}`;
  };

  render() {
    const {
      nomeEvento,
      descricao,
      dataInicio,
      dataFim,
      idEvento,
      valoresEvento
    } = this.props;

    return (
      <div className="container">
        <div className="card-panel z-depth-2">
          <div className="row">
            <div id="sobre" className="col s12">
              <br />
              <h3>{nomeEvento}</h3>
              <br />
              <h5>
                <i className="material-icons left">equalizer</i>Sobre o evento
              </h5>
              <p className="basicInfo grey-text text-darken-2">{descricao}</p>
              <br />
              <h5>
                <i className="material-icons left">info</i>Informações
                importantes
              </h5>
              <div className="basicInfo grey-text text-darken-2">
                <p>
                  <b>Data de ínicio:</b>

                  <span className="right">{formatDate(dataInicio)}</span>
                </p>
                <p>
                  <b>Data de término:</b>

                  <span className="right">{formatDate(dataFim)}</span>
                </p>
                {valoresEvento.map((valor, i) => (
                  <p key={i}>
                    <b>{valor.nome}</b>
                    <span className="right">
                      {this.formatValor(valor.preco)}
                    </span>
                  </p>
                ))}
              </div>
              <br />
              {new Date(dataFim) > Date.now() ? (
                <Link
                  to={`/pet/eventos/${idEvento}/cadastro`}
                  className={this.getStyleButton()}
                  style={{
                    textOverflow: 'ellipsis',
                    whiteSpace: 'nowrap',
                    overflow: 'hidden'
                  }}
                >
                  {this.getTextButton()}
                </Link>
              ) : null}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default sobreEvento;
