import React from 'react';
import {
  Edit,
  ReferenceInput,
  SimpleForm,
  SaveButton,
  Toolbar,
  TextInput,
  required,
  NumberInput
} from 'react-admin';
import BackButton from '../backButton';

const PostCreateToolbar = props => (
  <Toolbar {...props}>
    <SaveButton required={false} />
    <BackButton />
  </Toolbar>
);

export const PostCreate = props => (
  <Edit actions={null} title="Atividade/edit" {...props}>
    <SimpleForm toolbar={<PostCreateToolbar />}>
      <TextInput label="Categoria" source="nome" validate={required()} />
      <NumberInput
        label="Valor"
        source="preco"
        validate={[
          required(),
          minValue(1, 'Quantidade precisa ser pelo menos 1')
        ]}
      />
    </SimpleForm>
  </Edit>
);

export default PostCreate;
