import React from 'react';

import {
  ShowButton,
  ExportButton,
  ReferenceField,
  List,
  GET_MANY_REFERENCE,
  downloadCSV,
  CardActions,
  BooleanField,
  Datagrid,
  TextField
} from 'react-admin';

import DeleteButtonWithConfirmation from '../deleteButtonWithConfirmation';
import dataProvider from '../dataProvider';
import { unparse as convertToCSV } from 'papaparse/papaparse.min';
import Pagamento from '../statusPagamentoButton';
const exporter = async (records, fetchRelatedRecords) => {
  var userInfo = await [];
  let activityNames = [];
  let count = 0;
  const usuarios = await fetchRelatedRecords(
    records,
    'usuarioId',
    'Usuarios'
  ).then(async recordsUsuarios => {
    return recordsUsuarios;
  });
  await records.map(async element => {
    let info = {};
    let atividadesECargaHorarias = await dataProvider(
      GET_MANY_REFERENCE,
      'Inscricoes',
      {
        target: 'inscritoId',
        pagination: { page: 1, perPage: 0 },
        sort: { field: 'id', order: 'DESC' },
        id: element.id
      }
    ).then(async response => {
      let atividadesECargaHorarias = await fetchRelatedRecords(
        response.data,
        'atividadeId',
        'Atividades'
      ).then(async recordsAtividades => {
        return await response.data.map(record => [
          [recordsAtividades[record.atividadeId].nome], //nome da atividade
          record.cargaHoraria //carga horaria que o usuario teve na atividade
        ]);
      });
      return await atividadesECargaHorarias;
    });

    info['nome'] = await usuarios[element.usuarioId].nome; // nome do usuario
    info['email'] = await usuarios[element.usuarioId].email; // email do usuario
    info['cpf'] = await usuarios[element.usuarioId].cpf; // cpf do usuario
    info['cargaHorariaTotal'] = await element.cargaHoraria; // carga hoaria total do evento
    userInfo.push(info);

    atividadesECargaHorarias.forEach(activity => {
      info[activity[0]] = activity[1]; // cria uma chave com o nome da atividade e a carga horaria recebida
      if (!activityNames.includes(activity[0])) {
        activityNames.push(activity[0]); // insere o nome da atividade
      }
    });
    count = count + 1;
    if (count === records.length) {
      const csv = convertToCSV({
        data: userInfo,
        fields: ['nome', 'email', 'cpf', 'cargaHorariaTotal'].concat(
          activityNames
        )
      });

      downloadCSV(csv, 'Usuarios');
    }
  });
  // console.log(userInfo);
  //     const csv = convertToCSV({
  //       data: userInfo,
  //       fields: ["nome", "email", "cpf", "cargaHoraria"]
  //     });
  //     console.log(csv);
  //     downloadCSV(csv, "Usuarios");
  //nao da certo colocar aqui e pq eh async
};

const PostActions = ({ currentSort, eventId, resource }) => (
  <CardActions>
    <ExportButton
      resource={resource}
      sort={currentSort}
      filter={{ eventoId: eventId }}
      exporter={exporter}
    />
  </CardActions>
);

export const AjudanteList = ({ permissions, ...props }) => (
  <List
    title="Participantes"
    filter={{ eventoId: props.location.hash.substring(12) }}
    bulkActionButtons={false}
    actions={
      <PostActions {...{ eventId: props.location.hash.substring(10) }} />
    }
    {...props}
  >
    <Datagrid {...props}>
      {console.log(props)}
      <ReferenceField
        linkType={false}
        label="Nome"
        reference="Usuarios"
        source="usuarioId"
      >
        <TextField source="nome" />
      </ReferenceField>
      <ReferenceField
        linkType={false}
        label="CPF"
        reference="Usuarios"
        source="usuarioId"
      >
        <TextField source="cpf" />
      </ReferenceField>
      <ReferenceField
        label="E-mail"
        linkType={false}
        reference="Usuarios"
        source="usuarioId"
      >
        <TextField source="email" />
      </ReferenceField>
      <BooleanField label="Pagamento" source="pago" />
      <TextField label="Carga horÃ¡ria total" source="cargaHoraria" />
      {(permissions === 'Petiano' || permissions === 'admin') && (
        <Pagamento redirect={false} label="Status pagamento" undoable={true} />
      )}
      <ShowButton label="Visualizar atividades" />
      {permissions === 'admin' && (
        <DeleteButtonWithConfirmation
          redirect={false}
          label="Remover inscrito"
          undoable={true}
        />
      )}
    </Datagrid>
  </List>
);

export default AjudanteList;
