import React from 'react';
import {
  Datagrid,
  TextField,
  BooleanField,
  ShowButton,
  DateField,
  List,
  Link,
  ImageField,
  EditButton
} from 'react-admin';
import Button from '@material-ui/core/Button';
import IconContentAdd from '@material-ui/icons/People';
import DeleteButtonWithConfirmation from '../deleteButtonWithConfirmation';
import AddPicButton from '../addPicButton';

const ShowParticipantes = props => (
  <Button
    component={Link}
    {...props}
    to={{
      pathname: `/Inscritos/`,
      search: `${props.record.id}`
    }}
    label="Participantes"
  >
    <IconContentAdd /> Participantes
  </Button>
);

export const EventList = ({ permissions, ...props }) => (
  <List
    bulkActionButtons={false}
    sort={{ field: 'anoEvento', order: 'ASC' }}
    {...props}
  >
    <Datagrid>
      <ImageField className="thumbNailView" source="foto" />
      <TextField source="nome" />
      <DateField source="anoEvento" options={{ year: 'numeric' }} />
      <DateField locales="pt-BR" label="Início evento" source="dataInicio" />
      <DateField locales="pt-BR" label="Término evento" source="dataFim" />
      <DateField
        locales="pt-BR"
        label="Início inscrições"
        source="inicioInscricoes"
      />
      <DateField
        locales="pt-BR"
        label="Término inscrições"
        source="fimInscricoes"
      />
      <BooleanField source="gratuito"></BooleanField>
      <AddPicButton {...props}></AddPicButton>
      <ShowButton label="Visualizar atividades" />
      <ShowParticipantes label="Participantes" {...props}></ShowParticipantes>
      <EditButton label="Gerenciar dados" />
      {permissions && permissions === 'admin' && (
        <DeleteButtonWithConfirmation label="Remover evento" undoable={true} />
      )}
    </Datagrid>
  </List>
);

export default EventList;
