import React, { Fragment } from 'react';
import {
  Edit,
  List,
  ReferenceField,
  EditController,
  CardActions,
  ReferenceManyField,
  Datagrid,
  TextField
} from 'react-admin';
import BackButton from '../backButton';
import DeleteButtonWithConfirmation from '../deleteButtonWithConfirmation';
const PostActions = props => (
  <CardActions>
    <BackButton />
  </CardActions>
);

export const PostCreate = ({ permissions, ...props }) => (
  <EditController {...props}>
    {({ resource, record, redirect, save, basePath, version }) => {
      return (
        <Fragment>
          <Edit actions={null} title="Dia/" {...props}>
            <ReferenceManyField
              {...props}
              basePath={basePath}
              resource={resource}
              record={record}
              reference="Presencas"
              target="diaId"
              title={false}
            >
              <List
                actions={<PostActions {...{ turmaId: props.id }} />}
                title="Presença"
                bulkActionButtons={false}
                filter={{ diaId: props.id }}
                {...props}
              >
                <Datagrid>
                  <ReferenceField
                    label="Nome"
                    reference="Inscricoes"
                    source="inscricaoId"
                    linkType={false}
                  >
                    <ReferenceField
                      label="Nome"
                      reference="Inscritos"
                      source="inscritoId"
                      linkType={false}
                    >
                      <ReferenceField
                        label="Nome"
                        reference="Usuarios"
                        linkType={false}
                        source="usuarioId"
                      >
                        <TextField source="nome" />
                      </ReferenceField>
                    </ReferenceField>
                  </ReferenceField>

                  <ReferenceField
                    label="CPF"
                    reference="Inscricoes"
                    source="inscricaoId"
                    linkType={false}
                  >
                    <ReferenceField
                      label="CPF"
                      reference="Inscritos"
                      linkType={false}
                      source="inscritoId"
                    >
                      <ReferenceField
                        label="Nome"
                        reference="Usuarios"
                        source="usuarioId"
                        linkType={false}
                      >
                        <TextField source="cpf" />
                      </ReferenceField>
                    </ReferenceField>
                  </ReferenceField>
                  <ReferenceField
                    label="Nome do responsável da presença"
                    reference="Usuarios"
                    source="responsavelPresencaId"
                    linkType={false}
                  >
                    <TextField source="nome" />
                  </ReferenceField>
                  <DeleteButtonWithConfirmation
                    redirect={false}
                    label="Remover presença"
                    undoable={true}
                  />
                </Datagrid>
              </List>
            </ReferenceManyField>
          </Edit>
        </Fragment>
      );
    }}
  </EditController>
);

export default PostCreate;
