import React from "react";
import {
  Edit,
  required,
  SimpleForm,
  Toolbar,
  SaveButton,
  TextInput,
  LongTextInput
} from "react-admin";
import RichTextInput from "ra-input-rich-text";

const PostCreateToolbar = props => (
  <Toolbar {...props}>
    <SaveButton />
  </Toolbar>
);
export const PostCreate = props => (
  <Edit title="Postagem/edit" {...props}>
    <SimpleForm toolbar={<PostCreateToolbar />}>
      <TextInput validate={required()} label="Título" source="titulo" />
      <LongTextInput
        validate={required()}
        label="Descrição"
        source="descricao"
      />
      <RichTextInput
        validate={required()}
        toolbar={[
          ["bold", "italic", "underline", "strike"],
          [{ script: "super" }, { script: "sub" }],
          [{ header: "1" }, { header: "2" }, "blockquote", "code-block"],
          [
            { list: "ordered" },
            { list: "bullet" },
            { indent: "-1" },
            { indent: "+1" }
          ],
          ["direction", { align: [] }],
          ["link", "image"],
          ["clean"]
        ]}
        source="conteudo"
      />
    </SimpleForm>
  </Edit>
);

export default PostCreate;
