/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../Usuario/userProfile.css";
import Axios from "axios";
import PathName from "../pathConst";
class atividadeEventoItem extends Component {
  componentWillMount() {
    const { atividade } = this.props;
    Axios.get(
      `http://${PathName}/pet/api/Categoria/${atividade.categoriaId}`
    ).then(res => this.setState({ categoria: res.data.nome }));
  }
  state = {
    categoria: ""
  };
  render() {
    const { atividade } = this.props;
    const { categoria } = this.state;
    return (
      <div className="row">
        <div className="col s12 ">
          <div className="card">
            <div className="card-content">
              <h5 className="card-title">
                <b>{atividade.nome}</b>
              </h5>
              <br />
              <div className="basicInfo grey-text text-darken-2">
                <p>
                  {atividade.descricao.length > 600
                    ? atividade.descricao.substring(0, 600) + "..."
                    : atividade.descricao}
                </p>
                <hr className="style-six" />
                <p>
                  <b>Pré-requisitos:</b>
                  <i className="material-icons left">school</i>
                  <span className="right">{atividade.preRequisitos}</span>
                </p>
                <hr className="style-six" />

                <p>
                  <b>Carga horária:</b>
                  <i className="material-icons left">access_time</i>
                  <span className="right">{atividade.cargaHoraria}</span>
                </p>
                <hr className="style-six" />
                <p>
                  <b>Categoria</b>
                  <i className="material-icons left">timeline</i>
                  <span className="right">{categoria}</span>
                </p>
              </div>
              <div className="card-action">
                <Link
                  to={`/pet/eventos/${atividade.eventoId}/atividades/${atividade.id}`}
                  className="btn-small blue lighten-1 z-depth-2 "
                  style={{ float: "right" }}
                >
                  Ver detalhes
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default atividadeEventoItem;
