import React, { Component, Fragment } from "react";
import Axios from "axios";
import PathName from "../pathConst";
import formatDate from "./formatDate";
import Swal from "sweetalert2";
import { css } from "@emotion/core";
import ClipLoader from "react-spinners/ClipLoader";
import { Row, Col } from "react-materialize";
import "./centerIfSmall.css";
import moment from "moment";
// eslint-disable-next-line no-unused-vars
import tz from "moment-timezone";
const override = css`
  display: block;
  margin: 0 auto;
  border-color: white;
`;
class turmaItem extends Component {
  componentWillMount() {
    const { turma, eventoId } = this.props;
    const userId = localStorage.getItem("user_id");
    const token = localStorage.getItem("access_token");
    const Token = {
      headers: {
        Authorization: token
      }
    };
    this.setState({ Token: Token, userId: userId });
    const userFilter = {
      where: {
        usuarioId: userId,
        eventoId
      }
    };
    Axios.get(`http://${PathName}/pet/api/Turmas/${turma.id}/dias`).then(
      res => {
        this.setState({ dias: res.data });
      }
    );

    Axios.get(
      `http://${PathName}/pet/api/Inscritos?filter=${JSON.stringify(
        userFilter
      )}`,
      Token
    ).then(res => {
      if (res.data.length !== 0) {
        this.setState({ inscritoId: res.data[0].id });
      }
    });
    if (userId === null) {
      this.setState({ userLogged: false });
    } else {
      this.setState({ userLogged: true });
    }
  }

  state = {
    dias: [],
    success: false,
    inscritoId: null,
    sendState: false,
    userLogged: false,
    Token: {},
    userId: ""
  };

  handleClick = () => {
    this.setState({ sendState: true });
    if (this.state.inscritoId === null) {
      this.setState({ sendState: false });
      return Swal.fire({
        type: "error",
        title: "Ops! algo deu errado",
        text:
          "Você precisa estar inscrito no evento para conseguir inscrever-se em uma atividade."
      });
    } else {
      const newInscricao = {
        pago: false,
        cargaHoraria: 0,
        atividadeId: this.props.turma.atividadeId,
        inscritoId: this.state.inscritoId,
        turmaId: this.props.turma.id,
        usuarioId: this.state.userId
      };
      Axios.post(
        `http://${PathName}/pet/api/Inscricoes`,
        newInscricao,
        this.state.Token
      )
        .then(async res => {
          this.setState({ sendState: false, success: true });
          const status = await res.status;
          if (status >= 200 && status < 300) {
            return Swal.fire({
              type: "success",
              title: "Inscrição realizada com sucesso",
              text:
                "Foi enviado uma confirmação de inscrição da atividade para seu e-mail"
            });
          }
        })
        .catch(err => {
          this.setState({ sendState: false });
          return Swal.fire({
            type: "error",
            title: "Ops! algo deu errado",
            text: err.response.data.error.message
          });
        });
    }
  };

  getStyleButton = () => {
    const { success } = this.state;
    return !success ? "btn blue lighten-1 right" : "btn green right";
  };

  getTime = time => {
    return time.substring(11, 16);
  };

  buildRow = (icon, key, value) => (
    <Row>
      <Col s={12} m={6}>
        <i className="material-icons left">{icon}</i>
        <b className="left hide-on-small-and-down">{key}</b>
        <b className="really-center-span hide-on-med-and-up">{key}</b>
      </Col>

      <Col s={12} m={6}>
        <span className="right hide-on-small-and-down">{value}</span>
        <span className="really-center-span hide-on-med-and-up">{value}</span>
      </Col>
    </Row>
  );
  render() {
    const { turma } = this.props;
    const { dias } = this.state;
    return (
      <Fragment>
        {this.buildRow("map", "Local:", turma.nome)}
        <hr className="style-six" />
        {turma && turma.limited ? (
          <Fragment>
            {this.buildRow(
              "group",
              "Vagas:",
              `${turma.quantidadeAtualInscritos} / ${turma.quantidadeMaxInscritos}`
            )}
            <hr className="style-six" />
          </Fragment>
        ) : (
          <Fragment>
            {this.buildRow("group", "Vagas:", "Ilimitadas")}
            <hr className="style-six" />
          </Fragment>
        )}
        {dias.length <= 1
          ? dias.map(dia => (
              <Fragment key={dia.id}>
                {this.buildRow("event", "Data:", formatDate(dia.dia))}
                <hr className="style-six" />
                {this.buildRow(
                  "access_time",
                  "Horário:",
                  this.getTime(moment.tz(dia.dia, "America/Sao_Paulo").format())
                )}
                <hr className="style-six" />
              </Fragment>
            ))
          : dias.map((dia, i) => (
              <Fragment key={dia.id}>
                <h6>
                  <b>{`Informações do ${i + 1}º dia`}</b>
                </h6>
                {this.buildRow("event", "Data:", formatDate(dia.dia))}

                <hr className="style-six" />
                {this.buildRow(
                  "access_time",
                  "Horário:",
                  this.getTime(moment.tz(dia.dia, "America/Sao_Paulo").format())
                )}
                {i < dias.length - 1 ? <hr className="style-six" /> : null}
              </Fragment>
            ))}
        <div className="card-action">
          <div className="sweet-loading">
            <button
              onClick={this.handleClick}
              className={this.getStyleButton()}
              style={{
                textOverflow: "ellipsis",
                whiteSpace: "nowrap",
                overflow: "hidden",
                width: "150px"
              }}
            >
              {this.state.sendState ? (
                <ClipLoader
                  css={override}
                  sizeUnit={"px"}
                  size={25}
                  color={"white"}
                  loading={this.state.sendState}
                />
              ) : (
                "Inscrever-me"
              )}
            </button>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default turmaItem;
