import { connect } from 'react-redux';
import { change, submit, isSubmitting } from 'redux-form';
import React, { Component, Fragment } from 'react';
import {
  Button,
  SaveButton,
  CREATE,
  DELETE,
  withDataProvider
} from 'react-admin';
import IconContentAdd from '@material-ui/icons/ThumbUp';
import IconCancel from '@material-ui/icons/Cancel';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import PropTypes from 'prop-types';
import DialogActions from '@material-ui/core/DialogActions';

class PostQuickCreateButton extends Component {
  state = {
    error: false,
    showDialog: false
  };

  handleClick = () => {
    this.setState({ showDialog: true });
  };

  handleCloseClick = () => {
    this.setState({ showDialog: false });
  };

  handleSaveClick = () => {
    const { dataProvider, record, eventId } = this.props;
    console.log(this.props);
    dataProvider(
      CREATE,
      'Ajudantes',
      {
        pagination: { page: 1, perPage: 0 },
        sort: { field: 'id', order: 'DESC' },
        data: { usuarioId: record.usuarioId, eventoId: eventId },
        previousData: { pago: record.pago }
      },
      {
        onSuccess: {
          refresh: true
        },
        onError: {
          notification: {
            body: 'Error: algo deu errado!',
            level: 'warning'
          }
        }
      }
    ).then(res => {
      dataProvider(
        DELETE,
        'Solicitacoes',
        {
          pagination: { page: 1, perPage: 0 },
          sort: { field: 'id', order: 'DESC' },
          id: record.id
        },
        {
          onSuccess: {
            refresh: true
          },
          onError: {
            notification: {
              body: 'Error: algo deu errado!',
              level: 'warning'
            }
          }
        }
      );
    });
    this.setState({ showDialog: false });
  };

  render() {
    const { showDialog } = this.state;
    const { isSubmitting, basePath, record, resource } = this.props;

    return (
      <Fragment>
        <Button onClick={this.handleClick} label="Aceitar">
          <IconContentAdd />
        </Button>
        <Dialog
          fullWidth
          open={showDialog}
          onClose={this.handleCloseClick}
          aria-label="Aceitar"
        >
          <DialogTitle>Confirme para prosseguir com a operação</DialogTitle>
          <DialogActions>
            <SaveButton
              label="Confirmar"
              saving={isSubmitting}
              onClick={this.handleSaveClick}
            />
            <Button label="ra.action.cancel" onClick={this.handleCloseClick}>
              <IconCancel />
            </Button>
          </DialogActions>
        </Dialog>
      </Fragment>
    );
  }
}

PostQuickCreateButton.propTypes = {
  dataProvider: PropTypes.func.isRequired,
  dispatch: PropTypes.func.isRequired,
  record: PropTypes.object
};
const mapStateToProps = state => ({
  isSubmitting: isSubmitting('post-quick-create')(state)
});

const mapDispatchToProps = {
  change,

  submit
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withDataProvider(PostQuickCreateButton));
