import React, { Component, Fragment } from "react";
import Axios from "axios";
import PathName from "../pathConst";
import Header from "../Header/Header";
import CardTurmaItem from "./turmaItemWithCard";
import TurmaItem from "./turmaItemWithoutCard";
import { Link } from "react-router-dom";
import { Row, Col } from "react-materialize";
import "./centerIfSmall.css";

class atividadeItem extends Component {
  componentWillMount() {
    const { atividadeId } = this.props;
    const filterAtividade = {
      include: {
        relation: "categoria"
      }
    };
    Axios.get(
      `https://${PathName}/pet/api/Atividades/${atividadeId}?filter=${JSON.stringify(
        filterAtividade
      )}`
    ).then(res => {
      this.setState({
        nome: res.data.nome,
        descricao: res.data.descricao,
        cargaHoraria: res.data.cargaHoraria,
        valor: res.data.valor,
        categoria: res.data.categoria.nome
      });
    });
    Axios.get(
      `https://${PathName}/pet/api/Atividades/${atividadeId}/turmas`
    ).then(res => {
      this.setState({ turmas: res.data });
    });
  }

  state = {
    nome: "",
    descricao: "",
    cargaHoraria: "",
    valor: "",
    categoria: "",
    turmas: []
  };

  formatValor = valor => {
    return valor === 0 ? "Gratuito" : `R$ ${valor}`;
  };

  buildRow = (icon, key, value) => (
    <Row>
      <Col s={12} m={6}>
        <i className="material-icons left hide-on-small-and-down">{icon}</i>
        <b className="left hide-on-small-and-down">{key}</b>
        <b className="really-center-span hide-on-med-and-up">{key}</b>
      </Col>

      <Col s={12} m={6}>
        <span className="right hide-on-small-and-down">{value}</span>
        <span className="really-center-span hide-on-med-and-up">{value}</span>
      </Col>
    </Row>
  );
  render() {
    const {
      nome,
      descricao,
      cargaHoraria,
      valor,
      turmas,
      categoria
    } = this.state;
    const { eventoId } = this.props;
    const titleStyle = {
      fontWeight: "bold",
      fontSize: `${2}rem`,
      fontFamily: "Nunito"
    };
    return (
      <Fragment>
        <Header />
        <div className="container">
          <div className="card-panel z-depth-2">
            <div className="row">
              <div className="col s12">
                <h3 style={titleStyle} className="center">
                  {nome}
                </h3>
                <p style={{ textAlign: "justify" }}>{descricao}</p>
                <hr className="style-six" />
                <div className="basicInfo grey-text text-darken-2">
                  {this.buildRow("timeline", "Categoria:", categoria)}
                  <hr className="style-six" />
                  {this.buildRow("access_time", "Carga Horária:", cargaHoraria)}
                  <hr className="style-six" />
                  {this.buildRow(
                    "attach_money",
                    "Valor:",
                    this.formatValor(valor)
                  )}
                  <hr className="style-six" />
                  {turmas.length > 1 ? (
                    <div>
                      <h5 className="center">Turmas </h5>
                      {turmas.map(turma => (
                        <CardTurmaItem
                          turma={turma}
                          key={turma.id}
                          eventoId={eventoId}
                        />
                      ))}
                    </div>
                  ) : (
                    turmas.map((turma, i) => (
                      <TurmaItem turma={turma} key={i} eventoId={eventoId} />
                    ))
                  )}
                </div>
              </div>
              <div className="card-action">
                <Link
                  to={`/pet/eventos/${eventoId}/atividades`}
                  className="btn btn-small blue lighten-1 left"
                >
                  Voltar
                </Link>
              </div>
            </div>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default atividadeItem;
