import React from 'react';
import {
  Create,
  required,
  SimpleForm,
  ReferenceInput,
  Toolbar,
  SelectInput,
  AutocompleteArrayInput,
  ReferenceArrayInput
} from 'react-admin';
import BackButton from '../backButton';
import RealSaveButton from './CustomSaveButton';
const PostCreateToolbar = props => (
  <Toolbar {...props}>
    <RealSaveButton {...props}></RealSaveButton>
    <BackButton />
  </Toolbar>
);
const validateUserCreation = values => {
  const errors = {};
  if (!values.usuarioId) {
    errors.usuarioId = ['É necessário preencher pelo menos um email!'];
  } else if (new Set(values.usuarioId).size !== values.usuarioId.length) {
    errors.usuarioId = ['Não pode haver emails repetidos'];
  }
  if (!values.eventoId) {
    errors.eventoId = ['É necessário preencher o nome do evento!'];
  }

  return errors;
};

export const PostCreate = props => (
  <Create title="Ajudante/add" {...props}>
    <SimpleForm validate={validateUserCreation} toolbar={<PostCreateToolbar />}>
      <ReferenceArrayInput
        source="usuarioId"
        label="E-mail dos ajudantes"
        reference="Usuarios"
      >
        <AutocompleteArrayInput optionText="email" source="usuarioId" />
      </ReferenceArrayInput>
      <ReferenceInput
        label="Confirme o evento"
        source="eventoId"
        reference="Eventos"
      >
        <SelectInput source="eventoId" optionText="nome"></SelectInput>
      </ReferenceInput>
    </SimpleForm>
  </Create>
);

export default PostCreate;
