import React from 'react';

import {
  ShowButton,
  ExportButton,
  ReferenceField,
  List,
  CardActions,
  BooleanField,
  Datagrid,
  TextField
} from 'react-admin';

import DeleteButtonWithConfirmation from '../deleteButtonWithConfirmation';

import Pagamento from '../statusPagamentoButton';

const PostActions = ({ currentSort, eventId, resource }) => (
  <CardActions>
    <ExportButton
      resource={resource}
      sort={currentSort}
      filter={{ eventoId: eventId }}
    />
  </CardActions>
);

export const AjudanteList = ({ permissions, ...props }) => (
  <List title="Participantes" bulkActionButtons={false} {...props}>
    <Datagrid>
      <ReferenceField
        linkType={false}
        label="Nome"
        reference="Usuarios"
        source="usuarioId"
      >
        <TextField source="nome" />
      </ReferenceField>
      <ReferenceField
        linkType={false}
        label="CPF"
        reference="Usuarios"
        source="usuarioId"
      >
        <TextField source="cpf" />
      </ReferenceField>
      <ReferenceField
        label="E-mail"
        linkType={false}
        reference="Usuarios"
        source="usuarioId"
      >
        <TextField source="email" />
      </ReferenceField>
      <BooleanField label="Pagamento" source="pago" />
      <TextField label="Carga horÃ¡ria total" source="cargaHoraria" />
      {/* {(permissions === 'Petiano' || permissions === 'admin') && (
        <Pagamento redirect={false} label="Status pagamento" undoable={true} />
      )}
      <ShowButton label="Visualizar atividades" />
      {permissions === 'admin' && (
        <DeleteButtonWithConfirmation
          redirect={false}
          label="Remover inscrito"
          undoable={true}
        />
      )} */}
    </Datagrid>
  </List>
);

export default AjudanteList;
