import React, { Component, Fragment } from "react";
import "../userProfile.css";

class showProfile extends Component {
  componentWillMount() {
    localStorage.getItem("access_token")
      ? this.setState({ logged: true })
      : this.setState({ redirect: true });
  }

  state = {
    redirect: false,
    logged: false
  };

  getExtrasPetiano = () => {
    const { usuario } = this.props;
    if (localStorage.getItem("Petiano")) {
      return (
        <Fragment>
          <p>
            <b>GitHub:</b>
            {usuario.github ? (
              <span className="right">{usuario.github}</span>
            ) : (
              <span className="right">{"Não cadastrado"}</span>
            )}
          </p>
          <hr className="style-six" />
          <p>
            <b>LinkedIn:</b>
            {usuario.linkedin ? (
              <span className="right">{usuario.linkedin}</span>
            ) : (
              <span className="right">{"Não cadastrado"}</span>
            )}
          </p>
          <hr className="style-six" />
          <p>
            <b>Lattes:</b>
            {usuario.lattes ? (
              <span className="right">{usuario.lattes}</span>
            ) : (
              <span className="right">{"Não cadastrado"}</span>
            )}
          </p>
          <hr className="style-six" />
        </Fragment>
      );
    }
  };

  capitalizeString = str => {
    return this.state.logged
      ? str.toLowerCase().replace(/\b\w/g, l => l.toUpperCase())
      : null;
  };

  render() {
    const { usuario } = this.props;
    return (
      <div id="perfil" className="container">
        <div className="card-panel">
          <div className="row">
            <div className="col s12">
              <h5>
                <i className="material-icons left">person</i>Perfil
              </h5>
              <div className="basicInfo grey-text text-darken-2">
                <p>
                  <b>Nome:</b>
                  <span className="right">
                    {this.capitalizeString(usuario.nome)}
                  </span>
                </p>
                <hr className="style-six" />
                <p>
                  <b>CPF:</b>
                  <span className="right">{usuario.cpf}</span>
                </p>
                <hr className="style-six" />
                <p>
                  <b>E-mail:</b>
                  <span className="right">{usuario.email}</span>
                </p>
                <hr className="style-six" />
                <p>
                  <b>Username:</b>
                  <span className="right">{usuario.username}</span>
                </p>
                <hr className="style-six" />
                {this.getExtrasPetiano()}
              </div>
              <button
                type="button"
                className="btn blue lighten-1 right"
                onClick={this.props.handleClickEditar}
              >
                Editar perfil
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default showProfile;
