import React from "react";
import formatDate from "../Eventos/formatDate";
import "../Eventos/centerIfSmall.css";
import { Row, Col } from "react-materialize";
import moment from "moment";
// eslint-disable-next-line no-unused-vars
import tz from "moment-timezone";

const showInfo = props => {
  const buildRow = (key, value) => (
    <Row>
      <Col s={12} m={6}>
        <b className="left hide-on-small-and-down">{key}</b>
        <b className="really-center-span hide-on-med-and-up">{key}</b>
      </Col>

      <Col s={12} m={6}>
        <span className="right hide-on-small-and-down">{value}</span>
        <span className="really-center-span hide-on-med-and-up">{value}</span>
      </Col>
    </Row>
  );

  return (
    <div id="perfil" className="container">
      <div className="card-panel">
        <div className="row">
          <div className="col s12">
            <h5>
              <i className="material-icons left">info</i>Confirme as informações
            </h5>
            <div className="basicInfo grey-text text-darken-2">
              {props.usuario.nome ? (
                <span style={{ textTransform: "capitalize" }}>
                  {buildRow("Nome:", props.usuario.nome.toLowerCase())}
                </span>
              ) : null}
              <hr className="style-six" />
              {buildRow("E-mail:", props.usuario.email)}
              <hr className="style-six" />
              {buildRow("Evento:", props.evento.nome)}
              <hr className="style-six" />
              {buildRow(
                "Início do evento:",
                formatDate(
                  moment
                    .tz(String(props.evento.dataInicio), "America/Sao_Paulo")
                    .format()
                )
              )}
              <hr className="style-six" />
              {buildRow(
                "Término do evento:",
                formatDate(
                  moment
                    .tz(String(props.evento.dataFim), "America/Sao_Paulo")
                    .format()
                )
              )}
              <hr className="style-six" />
            </div>
            <div style={{ paddingTop: "2rem" }}>
              <button
                type="button"
                className="btn blue lighten-1 right"
                onClick={props.handleClickConfirmar}
              >
                Quero ser ajudante
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default showInfo;
