import React, { Component, Fragment } from 'react';
import axios from 'axios';
import Header from '../Header/Header';
import PathName from '../pathConst';
import RichTextEditor from 'react-rte';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import CardActions from '@material-ui/core/CardActions';
import dateParse from './dateBrFormat';

const styles = muiBaseTheme => ({
  card: {
    maxWidth: 900,
    overflow: 'auto',
    margin: 'auto',
    padding: 10,
    transition: '0.3s',
    boxShadow: '0 8px 40px -12px rgba(0,0,0,0.3)',
    '&:hover': {
      boxShadow: '0 16px 70px -12.125px rgba(0,0,0,0.3)'
    }
  },
  media: {
    paddingTop: '56.25%',
    marginTop: '30',
    height: 0
  },
  content: {
    textAlign: 'justify',
    padding: muiBaseTheme.spacing.unit * 3
  },
  divider: {
    margin: `${muiBaseTheme.spacing.unit * 3}px 0`
  },
  heading: {
    fontWeight: 'bold'
  },
  subheading: {
    lineHeight: 1.8
  },
  pos: {
    marginBottom: 12,
    textAlign: 'center'
  },
  text: {
    textAlign: 'center'
  }
});
export class postItem extends Component {
  constructor(props) {
    super(props);
    this.state = {
      titulo: '',
      conteudo: '',
      data: '',
      foto: null
    };
  }

  componentWillMount = async () => {
    await axios
      .get(`http://${PathName}/pet/api/Postagens/${this.props.id}`)
      .then(res =>
        this.setState({
          titulo: res.data.titulo,
          conteudo: res.data.conteudo,
          data: res.data.data,
          foto: res.data.foto
        })
      );
  };

  render() {
    const { classes } = this.props;
    const { titulo, conteudo, data, foto } = this.state;
    return (
      <div className="App">
        <Header />
        <Card className={classes.card}>
          {foto ? (
            <CardMedia className={classes.media} image={foto} />
          ) : (
            <CardMedia
              className={classes.media}
              image={
                'https://image.freepik.com/free-photo/river-foggy-mountains-landscape_1204-511.jpg'
              }
            />
          )}

          <CardContent>
            <Typography className={classes.text} gutterBottom variant={'h3'}>
              <h4>{titulo}</h4>
            </Typography>
            <Typography className={classes.pos} color="textSecondary">
              {dateParse(data)}
            </Typography>
            <RichTextEditor
              readOnly={true}
              value={RichTextEditor.createValueFromString(conteudo, 'html')}
            />
          </CardContent>
          <CardActions className={classes.content} disableSpacing></CardActions>
        </Card>
      </div>
    );
  }
}

export default withStyles(styles)(postItem);
