import React from "react";
import { css } from "@emotion/core";
import ClipLoader from "react-spinners/ClipLoader";
import { Link } from "react-router-dom";
import formatDate from "../../Eventos/formatDate";

const eventoAtual = props => {
  const getStyleButton = props => {
    const { success } = props;
    return success ? "btn green " : "btn btn-small red ";
  };

  const getIconPagamento = () => {
    return props.pago ? (
      <i className="material-icons right">check</i>
    ) : (
      <i className="material-icons right">close</i>
    );
  };

  const verifyShowPagamento = props => {
    const { evento } = props;
    return !evento.gratuito ? (
      <span>
        <hr className="style-six" />
        <p>
          <b>Pagamento:</b>
          {getIconPagamento(props)}
        </p>
      </span>
    ) : null;
  };

  return (
    <div className="card  hoverable">
      <div className="card-content">
        <span className="card-title">{props.evento.nome}</span>

        <div className="basicInfo grey-text text-darken-2">
          <p>
            <b>Data início:</b>
            <span className="right">{formatDate(props.evento.dataInicio)}</span>
          </p>
          <hr className="style-six" />
          <p>
            <b>Data término:</b>
            <span className="right">{formatDate(props.evento.dataFim)}</span>
          </p>
          <hr className="style-six" />
          <p>
            <b>Carga horária total de participação:</b>
            <span className="right">{props.cargaHoraria}</span>
          </p>
          {verifyShowPagamento(props)}
        </div>
      </div>
      <div className="card-action">
        <Link
          type="button"
          className="btn btn-small blue"
          to={`/pet/meusEventos/${props.evento.id}/Atividades`}
        >
          Visualizar atividades
        </Link>
      </div>

      <div className="card-action">
        <div className="sweet-loading">
          <button
            onClick={props.handleClick}
            className={getStyleButton(props)}
            style={{
              textOverflow: "ellipsis",
              whiteSpace: "nowrap",
              overflow: "hidden",
              width: "185px"
            }}
          >
            {props.sendState ? (
              <ClipLoader
                css={override}
                sizeUnit={"px"}
                size={25}
                color={"white"}
                loading={props.sendState}
              />
            ) : (
              "  Desinscrever-se"
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

const override = css`
  display: block;
  margin: 0 auto;
  border-color: white;
`;

export default eventoAtual;
