import React, { Component, Fragment } from "react";
import PostPreview, { postPreview } from "./postPreview.js";
import Header from "../Header/Header";
import axios from "axios";
import { GridList, GridListTile } from "@material-ui/core";
import PathName from "../pathConst.js";
import CardError from "../cardError";

import withWidth from "@material-ui/core/withWidth";

import compose from "recompose/compose";
export class showPost extends Component {
  constructor(props) {
    super(props);
    this.state = {
      blogs: []
    };
  }

  componentWillMount() {
    const filterPostagens = {
      fields: {
        conteudo: false
      }
    };
    axios
      .get(
        `http://${PathName}/pet/api/Postagens?filter=${JSON.stringify(
          filterPostagens
        )}`
      )
      .then(res => {
        this.setState({ blogs: res.data });
      });
  }

  render() {
    const { blogs } = this.state;
    const { width } = this.props;
    let columns = width === "xs" || width === "sm" ? 1 : 3;
    if (blogs.length === 0) {
      return (
        <Fragment>
          <Header />
          <CardError
            titulo={"Não consegui encontrar nenhum blog"}
            mensagem={
              "No momento, podemos estar passando por alguma manutenção, volte mais tarde para conferir os blogs que nós preparamos para você."
            }
          />
        </Fragment>
      );
    }
    return (
      <Fragment>
        <Header />
        <div className="container">
          <GridList cellHeight={"auto"} cols={columns}>
            {blogs.reverse().map(post => (
              <GridListTile key={post.id}>
                <PostPreview post={post} key={post.id}></PostPreview>
              </GridListTile>
            ))}
          </GridList>
        </div>
      </Fragment>
    );
  }
}

export default compose(withWidth())(showPost);
