import React, { Component } from 'react';
import { Admin, Resource } from 'react-admin';
import PostList from './Postagem/Post';
import addPost from './Postagem/addPost';
import editPost from './Postagem/editPost';
import UserList from './Usuario/Users';

import showActivity from './Atividade/showActivities';
import addActivity from './Atividade/addActivity';
import editActivity from './Atividade/editActivity';
import showTurma from './Turma/showTurma';
import editDadoAdicional from './DadoAdicional/editDadoAdicional';
import editTurma from './Turma/editTurma';
import showDia from './Dia/showDia';
import editDia from './Dia/editDia';
import EventList from './Evento/Events';
import addEvent from './Evento/addEvent';
import editEvent from './Evento/editEvent';
import showInscrito from './Inscrito/showInscrito';
import loopbackClient from 'react-admin-loopback';
import authProvider from './authProvider';
import LoginPage from '../Login/Login';
import Menu from './Menu';
import Dashboard from './Dashboard';
import Swal from 'sweetalert2';
import AjudanteList from './Ajudante/Ajudantes';
import PathName from '../pathConst';
import { ShowEvent } from './Evento/showEvent';
import CategoriaList from './Categoria/Categorias';
import editValor from './Valor/editValor';
import addCategoria from './Categoria/addCategoria';
import editOpcao from './Opcao/editOpcao';
import addAjudante from './Ajudante/addAjudante';
import englishMessages from 'ra-language-english';
import portugueseMessages from 'ra-language-portuguese';
import LogoutButton from './logoutButton';

const fetchJson = async (url, options = {}) => {
  const requestHeaders =
    options.headers ||
    new Headers({
      Accept: 'application/json'
    });
  if (
    !requestHeaders.has('Content-Type') &&
    !(options && options.body && options.body instanceof FormData)
  ) {
    requestHeaders.set('Content-Type', 'application/json');
  }
  if (options.user && options.user.authenticated && options.user.token) {
    requestHeaders.set('Authorization', options.user.token);
  }
  const response = await fetch(url, { ...options, headers: requestHeaders });
  const text = await response.text();
  const o = {
    status: response.status,
    statusText: response.statusText,
    headers: response.headers,
    body: text
  };
  let status = o.status,
    headers = o.headers,
    body = o.body;
  let json;
  try {
    json = JSON.parse(body);
  } catch (e) {
    // not json, no big deal
  }
  if (status < 200 || status >= 300) {
    if (typeof json.error.message !== 'object') {
      Swal.fire({
        type: 'error',
        title: 'Ops! algo deu errado',
        text: json.error.message
      });
    } else {
      let texto = json.error.message.map(e => e.message);
      Swal.fire({
        type: 'error',
        title: 'Ops! algo deu errado',
        text: texto
      });
    }
  }
  return Promise.resolve({
    status: status,
    headers: headers,
    body: body,
    json: json
  });
};
const httpClient = (url, options = {}) => {
  if (!options.headers) {
    options.headers = new Headers({ Accept: 'application/json' });
  }
  const token = localStorage.getItem('access_token');
  options.headers.set('Authorization', token);
  return fetchJson(url, options);
};

const dataProvider = loopbackClient(`http://${PathName}/pet/api`, httpClient);
const messages = {
  pt: portugueseMessages,
  en: englishMessages
};
const i18nProvider = locale => messages[locale];
class Manager extends Component {
  render() {
    return (
      <Admin
        logoutButton={LogoutButton}
        locale="pt"
        i18nProvider={i18nProvider}
        dataProvider={dataProvider}
        authProvider={authProvider}
        menu={Menu}
        dashboard={Dashboard}
        loginPage={LoginPage}
      >
        {permissions => [
          permissions === 'Petiano' || permissions === 'admin' ? (
            <Resource
              name="Postagens"
              list={PostList}
              create={addPost}
              edit={editPost}
            />
          ) : null,
          permissions === 'Petiano' ||
          permissions === 'admin' ||
          permissions === 'Ajudante' ? (
            <Resource name="Usuarios" list={UserList} />
          ) : null,
          <Resource
            name="Atividades"
            create={
              permissions === 'Petiano' || permissions === 'admin'
                ? addActivity
                : null
            }
            edit={
              permissions === 'Petiano' || permissions === 'admin'
                ? editActivity
                : null
            }
            show={showActivity}
          />,
          <Resource
            name="Turmas"
            show={showTurma}
            edit={
              permissions === 'Petiano' || permissions === 'admin'
                ? editTurma
                : null
            }
          />,
          <Resource
            name="Inscritos"
            show={
              permissions === 'Petiano' || permissions === 'admin'
                ? showInscrito
                : null
            }
          />,
          <Resource name="Presencas" />,
          <Resource name="Inscricoes" />,
          <Resource
            name="Eventos"
            list={EventList}
            create={
              permissions === 'Petiano' || permissions === 'admin'
                ? addEvent
                : null
            }
            show={ShowEvent}
            edit={
              permissions === 'Petiano' || permissions === 'admin'
                ? editEvent
                : null
            }
          />,
          <Resource
            name="Dias"
            show={showDia}
            edit={
              permissions === 'Petiano' || permissions === 'admin'
                ? editDia
                : null
            }
          />,
          <Resource
            name="Ajudantes"
            create={addAjudante}
            list={AjudanteList}
          />,
          permissions === 'Petiano' || permissions === 'admin' ? (
            <Resource
              name="Categoria"
              list={CategoriaList}
              create={addCategoria}
            />
          ) : null,
          permissions === 'Petiano' || permissions === 'admin' ? (
            <Resource name="DadosAdicionais" edit={editDadoAdicional} />
          ) : null,
          permissions === 'Petiano' || permissions === 'admin' ? (
            <Resource name="Opcoes" edit={editOpcao} />
          ) : null,
          permissions === 'Petiano' || permissions === 'admin' ? (
            <Resource name="Respostas" />
          ) : null,
          permissions === 'Petiano' || permissions === 'admin' ? (
            <Resource name="Valores" edit={editValor} />
          ) : null,
          permissions === 'Petiano' || permissions === 'admin' ? (
            <Resource name="Solicitacoes" />
          ) : null
        ]}
      </Admin>
    );
  }
}

export default Manager;
