import React, { Component } from "react";
import Axios from "axios";
import PathName from "../../pathConst";

import TextOption from "./dadoAdicionalText";
import BoolOption from "./dadoAdicionalBool";
import SingleBoolOption from "./dadoAdicionalSingleBool";

export default class categoriaDadoAdicional extends Component {
  state = {
    opcoes: []
  };

  componentWillMount() {
    let filterDadoAdicional = {
      where: {
        dadoAdicionalId: this.props.dadoAdicional.id
      }
    };

    Axios.get(
      `http://${PathName}/pet/api/Opcoes?filter=${JSON.stringify(
        filterDadoAdicional
      )}`
    ).then(res => {
      this.setState({
        opcoes: res.data
      });
    });
  }

  render() {
    const {
      dadoAdicional,
      opcoesSelecionadas,
      opcaoSelecionada,
      respostaTexto
    } = this.props;
    const { opcoes } = this.state;
    if (dadoAdicional.respIsBoolean) {
      return dadoAdicional.selectChoice === "selectInput" ? (
        <SingleBoolOption
          nome={dadoAdicional.nome}
          desc={dadoAdicional.descricao}
          opcoes={opcoes}
          dadoAdicionalId={dadoAdicional.id}
          opcaoSelecionada={opcaoSelecionada}
          handleChangeSingleOption={this.props.handleChangeSingleOption}
        />
      ) : (
        <BoolOption
          nome={dadoAdicional.nome}
          desc={dadoAdicional.descricao}
          opcoes={opcoes}
          dadoAdicionalId={dadoAdicional.id}
          opcoesSelecionadas={opcoesSelecionadas}
          handleChangeOption={this.props.handleChangeOption}
        />
      );
    } else {
      return (
        <TextOption
          nome={dadoAdicional.nome}
          desc={dadoAdicional.descricao}
          dadoAdicionalId={dadoAdicional.id}
          opcoes={opcoes}
          respostaTexto={respostaTexto}
          handleChangeText={this.props.handleChangeText}
        />
      );
    }
  }
}
