import React, { Component } from "react";
import AtividadesEventoItem from "./atividadeEventoItem";
import CardError from "../cardError";

class atividadesEvento extends Component {
  sortAtividadesByCategoria = arr => {
    return arr.sort((a, b) => (a.categoria.nome >= b.categoria.nome ? 1 : -1));
  };
  render() {
    const { atividades } = this.props;
    this.sortAtividadesByCategoria(atividades);
    if (atividades.length === 0) {
      return (
        <CardError
          titulo={"Esse evento ainda não possui atividades cadastradas"}
        />
      );
    } else {
      return (
        <div className="container">
          <div className="card-panel z-depth-2">
            <h3 style={{ textAlign: "center" }}>Atividades do Evento </h3>
            <div className="row">
              <div id="atividades" className="col s12">
                {atividades.map(atividade => (
                  <AtividadesEventoItem
                    atividade={atividade}
                    key={atividade.id}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>
      );
    }
  }
}

export default atividadesEvento;
