import React, { Component, Fragment } from "react";
import formatDate from "../../Eventos/formatDate";
import Swal from "sweetalert2";
import Axios from "axios";
import PathName from "../../pathConst";
import { Link } from "react-router-dom";
import { css } from "@emotion/core";
import ClipLoader from "react-spinners/ClipLoader";
const override = css`
  display: block;
  margin: 0 auto;
  border-color: white;
`;
class eventoItem extends Component {
  state = {
    redirectAtividades: false,
    sendState: false,
    success: false
  };
  getIconPagamento = () => {
    return this.props.pago ? (
      <i className="material-icons right">check</i>
    ) : (
      <i className="material-icons right">close</i>
    );
  };

  verifyDateEvent = () => {
    const dataTerminoEvento = Date.parse(this.props.evento.dataFim);
    let dataAtual = new Date();
    dataAtual.setHours(0, 0, 0, 0);
    return dataTerminoEvento.valueOf() < dataAtual.valueOf();
  };

  handleClickAtividades = () => {
    this.setState({ redirectAtividades: true });
  };

  getStyleButton = () => {
    const { success } = this.state;
    return success ? "btn green " : "btn btn-small red ";
  };

  verifyShowPagamento = () => {
    const { evento } = this.props;
    return !evento.gratuito ? (
      <Fragment>
        <hr className="style-six" />
        <p>
          <b>Pagamento:</b>
          {this.getIconPagamento()}
        </p>
      </Fragment>
    ) : null;
  };

  handleClick = () => {
    const { evento, token, inscricaoId } = this.props;
    Swal.fire({
      title: `Deseja mesmo se desinscrever do evento ${evento.nome}?`,
      type: "warning",
      text: `Todas suas inscrições nas atividades do evento ${evento.nome} serão anuladas`,
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Confirmar",
      cancelButtonText: "Cancelar"
    }).then(result => {
      if (result.value) {
        this.setState({ sendState: true });
        Axios.delete(
          `http://${PathName}/pet/api/Inscritos/${inscricaoId}`,
          token
        )
          .then(res => {
            this.setState({ sendState: false, success: true });
            return Swal.fire({
              type: "success",
              title: `Você se desinscreveu do evento ${evento.nome} com sucesso`,
              showConfirmButton: false,
              timer: 1500
            }).then(() => {
              window.location.reload();
            });
          })
          .catch(err => {
            this.setState({ sendState: false });
            return Swal.fire({
              type: "error",
              title: "Ops! algo deu errado",
              text: err.response.data.error.message
            });
          });
      }
    });
  };
  render() {
    const { evento, cargaHoraria, token } = this.props;
    return (
      <div className="card  hoverable">
        <div className="card-content">
          <span className="card-title">
            {this.verifyDateEvent() ? (
              <span
                className="new badge deep-orange darken-3"
                data-badge-caption="Evento encerrado"
              />
            ) : null}
            {evento.nome}
          </span>

          <div className="basicInfo grey-text text-darken-2">
            <p>
              <b>Data início:</b>
              <span className="right">{formatDate(evento.dataInicio)}</span>
            </p>
            <hr className="style-six" />
            <p>
              <b>Data término:</b>
              <span className="right">{formatDate(evento.dataFim)}</span>
            </p>
            <hr className="style-six" />
            <p>
              <b>Carga horária total de participação:</b>
              <span className="right">{cargaHoraria}</span>
            </p>
            {this.verifyShowPagamento()}
          </div>
        </div>
        <div className="card-action">
          <Link
            type="button"
            className="btn btn-small blue"
            to={`/pet/meusEventos/${evento.id}/Atividades`}
          >
            Visualizar atividades
          </Link>
        </div>
        {this.verifyDateEvent() ? null : (
          <div className="card-action">
            <div className="sweet-loading">
              <button
                onClick={this.handleClick}
                className={this.getStyleButton()}
                style={{
                  textOverflow: "ellipsis",
                  whiteSpace: "nowrap",
                  overflow: "hidden",
                  width: "185px"
                }}
              >
                {this.state.sendState ? (
                  <ClipLoader
                    css={override}
                    sizeUnit={"px"}
                    size={25}
                    color={"white"}
                    loading={this.state.sendState}
                  />
                ) : (
                  "  Desinscrever-se"
                )}
              </button>
            </div>
          </div>
        )}
      </div>
    );
  }
}

export default eventoItem;
