import React from 'react';
import {
  Datagrid,
  TextField,
  EditButton,
  ReferenceField,
  DateField,
  CardActions,
  downloadCSV,
  ExportButton,
  ShowButton,
  NumberField,
  Show,
  CreateButton,
  ReferenceManyField,
  List
} from 'react-admin';
import BackButton from '../backButton';
import { unparse as convertToCSV } from 'papaparse/papaparse.min';
import DeleteButtonWithConfirmation from '../deleteButtonWithConfirmation';
const exporter = itens => {
  const csv = convertToCSV({
    data: itens,
    // select and order fields in the export
    fields: ['nome', 'categoria', 'anoAtividade', 'valor', 'cargaHoraria']
  });
  downloadCSV(csv, 'Atividades'); // download as 'posts.csv` file
};

const PostActions = (
  {
    bulkActions,
    basePath,
    currentSort,
    displayedFilters,
    filters,
    filterValues,
    onUnselectItems,
    eventId,
    resource,
    selectedIds,
    showFilter,
    permissions,
    total
  },
  props
) => (
  <CardActions>
    <BackButton />
    {permissions && (permissions === 'Petiano' || permissions === 'admin') && (
      <CreateButton resource={resource} basePath={basePath}></CreateButton>
    )}
    <ExportButton
      resource={resource}
      sort={currentSort}
      filter={{ eventoId: eventId }}
      exporter={exporter}
    />
  </CardActions>
);
export const ShowEvent = ({ permissions, ...props }) => (
  <Show actions={false} title="Evento/" {...props}>
    <ReferenceManyField title={false} reference="Atividades" target="eventoId">
      <List
        title="Atividades"
        actions={
          <PostActions
            {...{ eventId: props.id }}
            {...{ permissions: permissions }}
          />
        }
        filter={{ eventoId: props.id }}
        bulkActionButtons={false}
        sort={{ field: 'categoriaId', order: 'ASC' }}
        {...props}
      >
        <Datagrid>
          <TextField source="nome" />
          <ReferenceField
            reference="Categoria"
            source="categoriaId"
            linkType={false}
          >
            <TextField source="nome" />
          </ReferenceField>
          <DateField options={{ year: 'numeric' }} source="anoAtividade" />
          <NumberField
            options={{ style: 'currency', currency: 'BRL' }}
            source="valor"
          />
          <NumberField source="cargaHoraria" />
          <ShowButton label="Visualizar turmas" />
          {permissions === 'Petiano' && <EditButton label="Gerenciar dados" />}
          {permissions === 'admin' && (
            <DeleteButtonWithConfirmation
              redirect={false}
              label="Remover turma"
              undoable={true}
            />
          )}
        </Datagrid>
      </List>
    </ReferenceManyField>
  </Show>
);
