import React, { Component, Fragment } from "react";
import Header from "../Header/Header";
import CardsPetianos from "./CardsPetianos";
import Axios from "axios";
import PathName from "../pathConst";
import { GridList, GridListTile } from "@material-ui/core";
import withWidth from "@material-ui/core/withWidth";
import compose from "recompose/compose";
class Petianos extends Component {
  componentWillMount() {
    const filter = {
      order: {
        nome: "ASC"
      }
    };
    Axios.get(
      `http://${PathName}/pet/api/Usuarios/petianos?filter=${JSON.stringify(
        filter
      )}`
    ).then(res => this.setState({ petianos: res.data }));
  }

  state = {
    petianos: []
  };

  render() {
    const { petianos } = this.state;
    const { width } = this.props;
    const columns = width === "xs" || width === "sm" ? 1 : 3;
    return (
      <Fragment>
        <Header />
        <div className="container">
          <h3 className="center">Membros atuais</h3>
          <GridList cellHeight={"auto"} cols={columns}>
            {petianos.map((petiano, i) => (
              <GridListTile key={i}>
                <CardsPetianos petiano={petiano} />
              </GridListTile>
            ))}
          </GridList>
        </div>
      </Fragment>
    );
  }
}

export default compose(withWidth())(Petianos);
