import React, { Component, Fragment } from "react";
import Axios from "axios";
import pathName from "../pathConst";
import Header from "../Header/Header";
import SobreEvento from "./sobreEvento";
import AtividadesEvento from "./atividadesEvento";
import TabsEvento from "./tabsEvento";
class EventoItem extends Component {
  state = {
    nomeEvento: "",
    anoEvento: "",
    descricao: "",
    valor: "",
    dataInicio: "",
    dataFim: "",
    atividades: [],
    valoresEvento: [],
    sobreEvento: true,
    atividadesEvento: false
  };
  componentWillMount() {
    const idEvento = this.props.id;
    const filterEvento = {
      include: "valores"
    };
    Axios.all([
      Axios.get(`http://${pathName}/pet/api/Eventos/${idEvento}/atividades`),
      Axios.get(
        `http://${pathName}/pet/api/Eventos/${idEvento}?filter=${JSON.stringify(
          filterEvento
        )}`
      )
    ]).then(
      Axios.spread((atividadeRes, eventoRes) => {
        this.setState({
          atividades: atividadeRes.data,
          nomeEvento: eventoRes.data.nome,
          anoEvento: eventoRes.data.anoEvento,
          descricao: eventoRes.data.descricao,
          valor: eventoRes.data.valor,
          dataInicio: eventoRes.data.dataInicio,
          dataFim: eventoRes.data.dataFim,
          idEvento: eventoRes.data.id,
          valoresEvento: eventoRes.data.valores
        });
      })
    );
  }

  handleClickEvento = () => {
    this.setState({ sobreEvento: true, atividadesEvento: false });
  };

  handleClickAtividades = () => {
    this.setState({ sobreEvento: false, atividadesEvento: true });
  };

  render() {
    const {
      nomeEvento,
      descricao,
      valor,
      dataInicio,
      dataFim,
      atividades,
      idEvento,
      valoresEvento,
      sobreEvento
    } = this.state;
    if (sobreEvento) {
      return (
        <Fragment>
          <Header />
          <TabsEvento
            handleClickEvento={this.handleClickEvento}
            handleClickAtividades={this.handleClickAtividades}
          />
          <SobreEvento
            nomeEvento={nomeEvento}
            descricao={descricao}
            valor={valor}
            dataInicio={dataInicio}
            dataFim={dataFim}
            idEvento={idEvento}
            valoresEvento={valoresEvento}
          />
        </Fragment>
      );
    } else {
      return (
        <Fragment>
          <Header />
          <TabsEvento
            handleClickEvento={this.handleClickEvento}
            handleClickAtividades={this.handleClickAtividades}
          />
          <AtividadesEvento atividades={atividades} />
        </Fragment>
      );
    }
  }
}

export default EventoItem;
