import React, { Component, Fragment } from 'react';

import { Row, Col, Collection, CollectionItem } from 'react-materialize';
import { Redirect } from 'react-router-dom';
import formatDate from './formatDate';
import moment from 'moment';
// eslint-disable-next-line no-unused-vars
import tz from 'moment-timezone';
import '../Usuario/userProfile.css';
import './centerIfSmall.css';

export class eventoPreview extends Component {
  state = {
    redirect: false,
    idRedirect: null
  };

  handleClick = () => {
    this.setState({ idRedirect: this.props.evento.id, redirect: true });
  };

  buildRow = (key, value) => (
    <Row>
      <Col s={12} m={6}>
        <b className="left hide-on-small-and-down">{key}</b>
        <b className="really-center-span hide-on-med-and-up">{key}</b>
      </Col>

      <Col s={12} m={6}>
        <span className="right hide-on-small-and-down">{value}</span>
        <span className="really-center-span hide-on-med-and-up">{value}</span>
      </Col>
    </Row>
  );

  getText = (style, text) => (
    <Fragment>
      <h5 className="hide-on-small-and-down" style={style}>
        {text}
      </h5>
      <h5 className="hide-on-med-and-up center" style={style}>
        {text}
      </h5>
    </Fragment>
  );

  render() {
    const {
      nome,
      descricao,
      foto,
      dataInicio,
      dataFim,
      inicioInscricoes,
      fimInscricoes
    } = this.props.evento;
    const { redirect, idRedirect } = this.state;
    const titleStyle = {
      fontWeight: 'bold',
      fontSize: `${2}rem`,
      fontFamily: 'Nunito'
    };
    console.log(dataFim, fimInscricoes);
    if (redirect) {
      return <Redirect to={`/pet/eventos/${idRedirect}`} />;
    }

    return (
      <Fragment>
        <Row>
          <Col m={12} s={12}>
            <Collection
              className="hoverable"
              onMouseEnter={() => {
                document.body.style.cursor = 'pointer';
              }}
              onMouseLeave={() => {
                document.body.style.cursor = 'default';
              }}
              onClick={this.handleClick}
            >
              <CollectionItem className="avatar" style={{ padding: 10 }}>
                <div className="col s12 m2 l6 xl2 ">
                  <img
                    src={foto ? foto : fotoLambda}
                    alt=""
                    style={{
                      display: 'inline-block',
                      boxSizing: 'border-box',
                      outline: 0,
                      borderRadius: 100,
                      height: 100,
                      width: 100
                    }}
                  />
                </div>
                <div className="col m10 s12">
                  <div className="hoverOn">
                    {this.getText(titleStyle, nome)}
                    <div className="basicInfo grey-text text-darken-2">
                      <p>
                        {descricao.length > 400
                          ? descricao.substring(0, 400) + '...'
                          : descricao}
                      </p>

                      <hr className="style-six" />
                      {this.buildRow(
                        'Início do evento:',
                        formatDate(
                          moment.tz(dataInicio, 'America/Sao_Paulo').format()
                        )
                      )}

                      <hr className="style-six" />
                      {this.buildRow(
                        'Término do evento:',
                        formatDate(
                          moment.tz(dataFim, 'America/Sao_Paulo').format()
                        )
                      )}

                      {inicioInscricoes ? (
                        <Fragment>
                          <hr className="style-six" />
                          {this.buildRow(
                            'Início das inscrições:',
                            formatDate(
                              moment
                                .tz(inicioInscricoes, 'America/Sao_Paulo')
                                .format()
                            )
                          )}
                        </Fragment>
                      ) : null}

                      {fimInscricoes ? (
                        <Fragment>
                          <hr className="style-six" />
                          {this.buildRow(
                            'Término das inscrições:',
                            formatDate(
                              moment
                                .tz(fimInscricoes, 'America/Sao_Paulo')
                                .format()
                            )
                          )}
                        </Fragment>
                      ) : null}
                    </div>
                  </div>
                </div>
              </CollectionItem>
            </Collection>
          </Col>
        </Row>
      </Fragment>
    );
  }
}

export default eventoPreview;
