import React, { Component, Fragment } from "react";
import fotoLambda from "../../images/lambda.png";
import { Row, Col, Collection, CollectionItem } from "react-materialize";
import { Redirect } from "react-router-dom";
import formatDate from "./formatDate";
import "../Usuario/userProfile.css";
export class eventoPreview extends Component {
  state = {
    redirect: false,
    idRedirect: null
  };

  handleClick = () => {
    this.setState({ idRedirect: this.props.evento.id, redirect: true });
  };

  render() {
    const {
      nome,
      descricao,
      foto,
      dataInicio,
      dataFim,
      inicioInscricoes,
      fimInscricoes
    } = this.props.evento;
    const { redirect, idRedirect } = this.state;
    const titleStyle = {
      fontWeight: "bold",
      fontSize: `${2}rem`,
      fontFamily: "Nunito"
    };
    if (redirect) {
      return <Redirect to={`/pet/eventos/${idRedirect}`} />;
    }

    return (
      <Fragment>
        <Row>
          <Col m={12} s={12}>
            <Collection
              className="hoverable"
              onMouseEnter={() => {
                document.body.style.cursor = "pointer";
              }}
              onMouseLeave={() => {
                document.body.style.cursor = "default";
              }}
              onClick={this.handleClick}
            >
              <CollectionItem className="avatar" style={{ padding: 10 }}>
                <div className="col s12 m2 l6 xl2 ">
                  <img
                    src={foto ? foto : fotoLambda}
                    alt=""
                    style={{
                      display: "block",
                      boxSizing: "border-box",
                      outline: 0,
                      borderRadius: 100,
                      height: 100,
                      width: 100
                    }}
                  />
                </div>
                <div className="col m10 s12">
                  <div className="hoverOn">
                    <h5 style={titleStyle}>{nome}</h5>
                    <div className="basicInfo grey-text text-darken-2">
                      <p>
                        {descricao.length > 400
                          ? descricao.substring(0, 400) + "..."
                          : descricao}
                      </p>
                      <hr className="style-six" />
                      <p>
                        <b>Início do evento:</b>
                        <span className="right">{formatDate(dataInicio)}</span>
                      </p>
                      <hr className="style-six" />
                      <p>
                        <b>Término do evento:</b>
                        <span className="right">{formatDate(dataFim)}</span>
                      </p>
                      <hr className="style-six" />
                      {inicioInscricoes ? (
                        <Fragment>
                          <p>
                            <b>Início inscrições:</b>
                            <span className="right">
                              {formatDate(inicioInscricoes)}
                            </span>
                          </p>
                          <hr className="style-six" />
                        </Fragment>
                      ) : null}
                      {fimInscricoes ? (
                        <Fragment>
                          <p>
                            <b>Término inscrições:</b>
                            <span className="right">
                              {formatDate(fimInscricoes)}
                            </span>
                          </p>
                        </Fragment>
                      ) : null}
                      <br />
                    </div>
                  </div>
                </div>
              </CollectionItem>
            </Collection>
          </Col>
        </Row>
      </Fragment>
    );
  }
}

export default eventoPreview;
