import React, { Component, Fragment } from "react";
import Axios from "axios";
import Header from "../Header/Header";
import PathName from "../pathConst";
import ShowProfile from "./Perfil/showProfile";

import EditProfile from "./Perfil/editProfile";

const borderStyle = {
  width: "100%",
  letterSpacing: "1px",
  color: "#1e88e5",
  border: "1px solid #bdbdbd",
  borderRadius: "5px"
};
class userProfile extends Component {
  componentWillMount() {
    const userId = localStorage.getItem("user_id");
    const token = localStorage.getItem("access_token");
    const Token = {
      headers: {
        Authorization: token
      }
    };
    this.setState({ Token: Token });
    Axios.get(`http://${PathName}/pet/api/Usuarios/${userId}`, Token).then(
      res => {
        this.setState({ usuario: res.data });
      }
    );
  }

  state = {
    usuario: [],
    redirectToEdit: false,
    Token: {}
  };

  handleClickEditar = () => {
    this.setState({ redirectToEdit: true });
  };

  handleClickVisualizar() {
    this.setState({ redirectToEdit: false });
  }

  render() {
    const { usuario, redirectToEdit } = this.state;
    if (redirectToEdit) {
      return <EditProfile usuario={usuario} />;
    }

    return (
      <Fragment>
        <Header />
        <div className="container" style={{ marginBottom: "1rem" }}>
          <ul className="tabs">
            <li className="tab hoverable" style={borderStyle}>
              <span>Perfil</span>
            </li>
          </ul>
        </div>

        <ShowProfile
          usuario={usuario}
          handleClickEditar={this.handleClickEditar}
        />
      </Fragment>
    );
  }
}

export default userProfile;
