import React, { Component, Fragment } from 'react';
import Axios from 'axios';
import PathName from '../pathConst';
import formatDate from './formatDate';
import Swal from 'sweetalert2';
import { css } from '@emotion/core';
import IconCancel from '@material-ui/icons/Cancel';
import CheckCancel from '@material-ui/icons/Check';

// import { Rabbit as Button } from 'react-button-loaders';
import ClipLoader from 'react-spinners/ClipLoader';
const override = css`
  display: block;
  margin: 0 auto;
  border-color: white;
`;
class turmaItem extends Component {
  componentWillMount() {
    const { turma, eventoId } = this.props;
    const userId = localStorage.getItem('user_id');
    const token = localStorage.getItem('access_token');
    const Token = {
      headers: {
        Authorization: token
      }
    };

    this.setState({ Token: Token, userId: userId });
    const userFilter = {
      where: {
        usuarioId: userId,
        eventoId
      }
    };
    Axios.get(`http://${PathName}/pet/api/Turmas/${turma.id}/dias`).then(
      res => {
        this.setState({ dias: res.data });
      }
    );

    Axios.get(
      `http://${PathName}/pet/api/Inscritos?filter=${JSON.stringify(
        userFilter
      )}`,
      Token
    ).then(res => {
      if (res.data.length !== 0) {
        this.setState({ inscritoId: res.data[0].id });
      }
    });
    if (userId === null) {
      this.setState({ userLogged: false });
    } else {
      this.setState({ userLogged: true });
    }
  }

  state = {
    dias: [],
    inscritoId: null,
    userLogged: false,
    Token: {},
    sucesso: false,

    userId: '',
    sendState: false
  };

  handleClick = () => {
    this.setState({ sendState: true });
    if (this.state.inscritoId === null) {
      this.setState({ sendState: false });
      return Swal.fire({
        type: 'error',
        title: 'Ops! algo deu errado',
        text:
          'Você precisa estar inscrito no evento para conseguir inscrever-se em uma atividade.'
      });
    } else {
      const newInscricao = {
        pago: false,
        cargaHoraria: 0,
        atividadeId: this.props.turma.atividadeId,
        inscritoId: this.state.inscritoId,
        turmaId: this.props.turma.id,
        usuarioId: this.state.userId
      };
      Axios.post(
        `http://${PathName}/pet/api/Inscricoes`,
        newInscricao,
        this.state.Token
      )
        .then(async res => {
          this.setState({ sendState: false, success: true });
          const status = await res.status;
          if (status >= 200 && status < 300) {
            return Swal.fire({
              type: 'success',
              title: 'Inscrição realizada com sucesso',
              text:
                'Foi enviado uma confirmação de inscrição da atividade para seu e-mail'
            });
          }
        })
        .catch(err => {
          this.setState({ sendState: false });
          return Swal.fire({
            type: 'error',
            title: 'Ops! algo deu errado',
            text: err.response.data.error.message
          });
        });
    }
  };

  getStyleButton = () => {
    const { success } = this.state;
    return !success ? 'btn blue lighten-1 right' : 'btn green right';
  };

  getTextButton = () => {
    const { userLogged } = this.state;
    return userLogged ? 'Inscrever-se' : 'Efetue login p/ inscrever-se';
  };

  getTime = time => {
    return time.substring(11, 16);
  };

  render() {
    const { turma } = this.props;
    const { dias } = this.state;
    return (
      <div className="row">
        <div className="col s12 ">
          <div className="card">
            <div className="card-content">
              <p>
                <b>Local:</b>
                <i className="material-icons left">map</i>
                <span className="right">{turma.nome}</span>
              </p>
              <hr className="style-six" />
              {turma && turma.limited ? (
                <div>
                  <p>
                    <b>Vagas:</b>
                    <i className="material-icons left">group</i>
                    <span className="right">{`${turma.quantidadeAtualInscritos} / ${turma.quantidadeMaxInscritos}`}</span>
                  </p>
                  <hr className="style-six" />
                </div>
              ) : (
                <div>
                  <p>
                    <b>Vagas:</b>
                    <i className="material-icons left">group</i>
                    <span className="right">{`Sem limites`}</span>
                  </p>
                  <hr className="style-six" />
                </div>
              )}
              {dias.length <= 1
                ? dias.map((dia, i) => (
                    <Fragment key={i}>
                      <p>
                        <b>Data:</b>
                        <i className="material-icons left">event</i>
                        <span className="right">{formatDate(dia.dia)}</span>
                      </p>
                      <hr className="style-six" />
                      <b>Horário:</b>
                      <i className="material-icons left">access_time</i>
                      <span className="right">{this.getTime(dia.dia)}</span>
                      <hr className="style-six" />
                    </Fragment>
                  ))
                : dias.map((dia, i) => (
                    <Fragment key={i}>
                      <p>
                        <b>{`Informações do ${i + 1}º dia`}</b>
                      </p>
                      <hr className="style-six" />
                      <p>
                        <b>Data:</b>
                        <i className="material-icons left">event</i>
                        <span className="right">{formatDate(dia.dia)}</span>
                      </p>
                      <hr className="style-six" />
                      <b>Horário:</b>
                      <i className="material-icons left">access_time</i>
                      <span className="right">{this.getTime(dia.dia)}</span>
                      {i < dias.length - 1 && <hr className="style-six" />}
                    </Fragment>
                  ))}
              <div className="card-action">
                <div className="sweet-loading">
                  <button
                    onClick={this.handleClick}
                    className={this.getStyleButton()}
                    style={{
                      textOverflow: 'ellipsis',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      width: '150px'
                    }}
                  >
                    {this.state.sendState ? (
                      <ClipLoader
                        css={override}
                        sizeUnit={'px'}
                        size={25}
                        color={'white'}
                        loading={this.state.sendState}
                      />
                    ) : (
                      'Inscrever-me'
                    )}
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default turmaItem;
