import React, { Component, Fragment } from 'react';
import { Card } from './petianosStyle.js';
import fotoLambda from '../../images/lambda_transparent.png';
import Rodal from 'rodal';
import 'rodal/lib/rodal.css';
import CardModal from './cardModal';

class CardsPetianos extends Component {
  state = {
    showModal: false
  };

  show() {
    this.setState({ showModal: true });
  }

  hide() {
    this.setState({ showModal: false });
  }

  handleClick = () => {
    this.setState({ showModal: true });
  };

  getWidth = () => {
    const width = window.screen.width;
    if (width <= 600) {
      return 80;
    } else if (width <= 992) {
      return 70;
    } else {
      return 60;
    }
  };

  getStyleImg = () => {
    const width = window.screen.width;
    if (width <= 600) {
      const style = {
        display: 'block',
        height: '150px',
        boxSizing: 'border-box',
        outline: 0,
        borderRadius: 100,
        width: 'auto',
        marginLeft: 'auto',
        marginRight: 'auto'
      };
      return style;
    } else if (width <= 992) {
      const style = {
        display: 'block',
        height: '200px',
        boxSizing: 'border-box',
        outline: 0,
        borderRadius: 100,
        width: 'auto',
        marginLeft: 'auto',
        marginRight: 'auto'
      };
      return style;
    } else {
      const style = {
        display: 'block',
        height: '250px',
        boxSizing: 'border-box',
        outline: 0,
        borderRadius: 100,
        width: 'auto',
        marginLeft: 'auto',
        marginRight: 'auto'
      };
      return style;
    }
  };

  render() {
    const { petiano } = this.props;
    const { showModal } = this.state;

    return (
      <Fragment>
        <div className="container">
          <Card
            onMouseEnter={() => {
              document.body.style.cursor = 'pointer';
            }}
            onMouseLeave={() => {
              document.body.style.cursor = 'default';
            }}
            onClick={this.show.bind(this)}
          >
            <img
              src={petiano.foto ? petiano.foto : fotoLambda}
              className="circle responsive-img"
              alt="imagem do petiano"
              style={{
                display: 'block',
                height: '150px',
                boxSizing: 'border-box',
                outline: 0,
                borderRadius: 100,
                width: 'auto',
                marginLeft: 'auto',
                marginRight: 'auto'
              }}
            />
            <strong style={nameStyle} className="center-align">
              {petiano.nome
                .split(' ')[0]
                .charAt(0)
                .toUpperCase() + string.slice(1)}
            </strong>
          </Card>
          <Rodal
            showCloseButton={false}
            closeOnEsc
            visible={showModal}
            onClose={this.hide.bind(this)}
            width={0}
            height={0}
          >
            <div className="container">
              <div className="row">
                <CardModal petiano={petiano} />
              </div>
            </div>
          </Rodal>
        </div>
      </Fragment>
    );
  }
}

const nameStyle = {
  letterSpacing: '1px',
  color: '#1e88e5'
};

export default CardsPetianos;
