import React, { Fragment } from "react";
import { css } from "@emotion/core";
import ClipLoader from "react-spinners/ClipLoader";
import { Link } from "react-router-dom";
import formatDate from "../../Eventos/formatDate";
import { Row, Col } from "react-materialize";
import "../../Eventos/centerIfSmall.css";
const eventoAtual = props => {
  const getStyleButton = props => {
    const { success } = props;
    return success ? "btn green " : "btn btn-small red ";
  };

  const getIconPagamento = () => {
    return props.pago ? (
      <i className="material-icons right">check</i>
    ) : (
      <i className="material-icons right">close</i>
    );
  };

  const verifyShowPagamento = props => {
    const { evento } = props;
    return !evento.gratuito ? (
      <Fragment>{buildRow("Pagamento:", getIconPagamento(props))}</Fragment>
    ) : null;
  };

  const buildRow = (key, value) => (
    <Row>
      <Col s={12} m={6}>
        <b className="left hide-on-small-and-down">{key}</b>
        <b className="really-center-span hide-on-med-and-up">{key}</b>
      </Col>

      <Col s={12} m={6}>
        <span className="right hide-on-small-and-down">{value}</span>
        <span className="really-center-span hide-on-med-and-up">{value}</span>
      </Col>
    </Row>
  );
  return (
    <div className="card  hoverable">
      <div className="card-content">
        <span className="card-title" style={titleStyle}>
          {props.evento.nome}
        </span>
        <div className="basicInfo grey-text text-darken-2">
          {buildRow("Início do evento:", formatDate(props.evento.dataInicio))}

          <hr className="style-six" />
          {buildRow("Término do evento:", formatDate(props.evento.dataFim))}

          <hr className="style-six" />
          {buildRow("Carga horária:", props.cargaHoraria)}
          <hr className="style-six" />

          {verifyShowPagamento(props)}
        </div>
      </div>
      <div className="card-action">
        <Link
          type="button"
          className="btn btn-small blue"
          to={`/pet/meusEventos/${props.evento.id}/Atividades`}
        >
          Visualizar atividades
        </Link>
      </div>

      <div className="card-action">
        <div className="sweet-loading">
          <button
            onClick={props.handleClick}
            className={getStyleButton(props)}
            style={{
              textOverflow: "ellipsis",
              whiteSpace: "nowrap",
              overflow: "hidden",
              width: "185px"
            }}
          >
            {props.sendState ? (
              <ClipLoader
                css={override}
                sizeUnit={"px"}
                size={25}
                color={"white"}
                loading={props.sendState}
              />
            ) : (
              "  Desinscrever-se"
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

const override = css`
  display: block;
  margin: 0 auto;
  border-color: white;
`;

const titleStyle = {
  fontWeight: "bold",
  fontSize: `${2}rem`,
  fontFamily: "Nunito"
};

export default eventoAtual;
