import React, { Fragment, Component } from 'react';
import { Select } from 'react-materialize';

class dadoAdicionalBool extends Component {
  getValue = () => {
    const { opcoesSelecionadas, dadoAdicionalId } = this.props;
    return opcoesSelecionadas.filter(o => o.dadoAdicionalId === dadoAdicionalId);
  };

  render() {
    const { nome, desc, opcoes } = this.props;
    return (
      <Fragment>
        <h5>
          <i className="material-icons left">question_answer</i>
          {nome}
        </h5>
        <p className="basicInfo grey-text text-darken-2">{desc}</p>

        <Select
          s={12}
          multiple
          value={this.getValue().idOpcoes}
          onChange={this.props.handleChangeOption}
          name="opcoesSelecionadas"
        >
          <option value="" disabled defaultValue={this.getValue().idOpcoes}>
            Escolha dentre as opções
          </option>
          {opcoes.map(opcao => (
            <option key={opcao.id} value={opcao.id}>
              {opcao.valor}
            </option>
          ))}
        </Select>
        <br />
      </Fragment>
    );
  }
}

export default dadoAdicionalBool;
