import React from 'react';
import {
  Create,
  SimpleForm,
  TextInput,
  required,
  LongTextInput,
  BooleanInput
} from 'react-admin';
import Br from 'date-fns/locale/pt-BR';
import DateFnsUtils from '@date-io/date-fns';
import { DateInput } from 'react-admin-date-inputs';
DateFnsUtils.prototype.getStartOfMonth = DateFnsUtils.prototype.startOfMonth;
const validateUserCreation = values => {
  const errors = {};
  if (!values.nome) {
    errors.nome = ['É necessário preencher um nome!'];
  }
  if (values.valor === null) {
    errors.valor = ['São aceitos apenas números!'];
  } else if (values.valor < 0) {
    errors.valor = ['O valor precisa ser maior que ou igual a 0'];
  }

  if (!values.descricao) {
    errors.descricao = ['É necessário dar uma descrição à atividade'];
  }

  return errors;
};

export const PostCreate = props => (
  <Create title="Evento" {...props}>
    <SimpleForm validate={validateUserCreation}>
      <TextInput label="Nome do evento" source="nome" />
      <LongTextInput label="Descrição" source="descricao" />
      <DateInput
        label="Data início "
        source="dataInicio"
        providerOptions={{ utils: DateFnsUtils, locale: Br }}
      />
      <DateInput
        providerOptions={{ utils: DateFnsUtils, locale: Br }}
        label="Data término "
        source="dataFim"
      />
      <BooleanInput
        required={required()}
        defaultValue={false}
        label="Gratuito"
        source="gratuito"
      ></BooleanInput>
    </SimpleForm>
  </Create>
);

export default PostCreate;
