const formatDate = data => {
  let meses = [
    "Janeiro",
    "Fevereiro",
    "Março",
    "Abril",
    "Maio",
    "Junho",
    "Julho",
    "Agosto",
    "Setembro",
    "Outubro",
    "Novembro",
    "Dezembro"
  ];
  let semana = [
    "Domingo",
    "Segunda-feira",
    "Terça-feira",
    "Quarta-feira",
    "Quinta-feira",
    "Sexta-feira",
    "Sábado"
  ];
  let hoje = new Date(data);

  let dia = hoje.getDate();
  let dias = hoje.getDay();
  let mes = hoje.getMonth();
  let ano = hoje.getFullYear();
  let diaext = semana[dias] + ", " + dia + " de " + meses[mes] + " de " + ano;
  return diaext;
};

export default formatDate;
