import React from "react";
import "./cardPetiano.css";
import "../Usuario/userProfile.css";
import fotoLambda from "../../images/lambda_transparent.png";
const cardModal = props => {
  const { petiano } = props;
  const capitalizeString = str => {
    return str.toLowerCase().replace(/\b\w/g, l => l.toUpperCase());
  };
  return (
    <aside className="profile-card">
      <header>
        <a href="#">
          <img
            src={petiano.foto ? petiano.foto : fotoLambda}
            alt="foto_petiano"
          />
        </a>

        <h1>{capitalizeString(petiano.nome)}</h1>
      </header>

      <div className="profile-bio">
        <p className="center-align">
          <i className="fas fa-envelope">
            <span style={{ fontWeight: "normal" }}>{" " + petiano.email}</span>
          </i>
        </p>
        <hr className="style-six" />
        <div style={iconStyle}>
          <a
            href={petiano.github}
            rel="noopener noreferrer"
            target="_blank"
            style={{ fontWeight: "normal" }}
          >
            <i className="fab fa-3x fa-github "></i>
          </a>
          <a
            href={petiano.linkedin}
            rel="noopener noreferrer"
            target="_blank"
            style={{ fontWeight: "normal" }}
          >
            <i className="fab fa-3x fa-linkedin"></i>
          </a>
          <a
            href={petiano.lattes}
            rel="noopener noreferrer"
            target="_blank"
            style={{ fontWeight: "normal" }}
          >
            <i className="fas fa-3x fa-graduation-cap"></i>
          </a>
        </div>
        <p className="center-align" style={iconStyle}></p>
      </div>
    </aside>
  );
};

const iconStyle = {
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
  margin: "0px 10px 0px 10px"
};

export default cardModal;
