import React from 'react';
import { Title } from 'react-admin';
import compose from 'recompose/compose';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';

import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';

import HomeIcon from '@material-ui/icons/Home';

import { withStyles } from '@material-ui/core/styles';
import { translate } from 'react-admin';
import images from '../../images/banner.jpg';
const styles = {
  media: {
    height: '25em'
  }
};

const Welcome = ({ classes, translate }) => (
  <Card>
    <Title title="PET-Informática" />
    <CardMedia image={images} className={classes.media} />
    <CardActions style={{ justifyContent: 'flex-end' }}>
      <Button
        href={
          window.location.host.includes('din.uem.br')
            ? `http://din.uem.br/pet/`
            : `http://localhost:3000/pet/`
        }
      >
        <HomeIcon style={{ paddingRight: '0.5em' }} />
        {translate('Página inicial')}
      </Button>
    </CardActions>
  </Card>
);

const enhance = compose(
  withStyles(styles),
  translate
);

export default enhance(Welcome);
