import React, { Component, Fragment } from "react";
import "../userProfile.css";
import Axios from "axios";
import { Redirect } from "react-router-dom";
import PathName from "../../pathConst";
import EventoItem from "./eventoItem";
import CardError from "../../cardError";
import Header from "../../Header/Header";
import ReactLoading from "react-loading";
import Swal from "sweetalert2";
import TabEventosProfile from "./tabEventosProfile";

class showEventos extends Component {
  componentWillMount() {
    const usuarioId = localStorage.getItem("user_id");
    const token = localStorage.getItem("access_token");
    const Token = {
      headers: {
        Authorization: token
      }
    };
    const filterUser = {
      where: {
        usuarioId
      },
      include: {
        relation: "evento"
      }
    };
    Axios.get(
      `https://${PathName}/pet/api/Inscritos/?filter=${JSON.stringify(
        filterUser
      )}`,
      Token
    )
      .then(res => {
        this.setState({ inscricoes: res.data, Token, done: true });
      })
      .catch(err => {
        this.setState({ done: true });
        return Swal.fire({
          type: "error",
          title: "Ops! algo deu errado",
          text: token
            ? err.response.data.error.message
            : "Você precisa estar logado para verificar seus dados"
        });
      });
  }

  state = {
    inscricoes: [],
    redirectAtividades: false,
    Token: {},
    showEventosAtuais: true,
    done: false
  };

  handleClickAtividades = () => {
    this.setState({ redirectAtividades: true });
  };

  verifyDate = (dataFim, nome) => {
    let dataAtual = new Date();
    dataAtual.setHours(0, 0, 0, 0);
    dataFim = Date.parse(dataFim);
    return dataFim.valueOf() >= dataAtual.valueOf();
  };

  handleClickEventosAtivos = () => {
    this.setState({ showEventosAtuais: true });
  };

  handleClickEventosAnteriores = () => {
    this.setState({ showEventosAtuais: false });
  };

  render() {
    const {
      inscricoes,
      redirectAtividades,
      Token,
      done,
      showEventosAtuais
    } = this.state;
    if (!done) {
      return (
        <div style={styleLoading}>
          <ReactLoading type={"bars"} color={"#42a5f5"} />
        </div>
      );
    } else if (redirectAtividades) {
      return <Redirect to="/pet/meusEventos/Atividades" />;
    } else if (inscricoes.length === 0) {
      return (
        <Fragment>
          <Header />
          <CardError
            titulo={"Não consegui encontrar nenhum evento"}
            mensagem={
              "Aqui você encontraria todos os eventos que participou ou está partcipando, porém, parece que você não se inscreveu em nenhum evento até agora. "
            }
          />
        </Fragment>
      );
    } else {
      return (
        <Fragment>
          <Header />
          <div className="container" style={{ marginBottom: "1rem" }}>
            <TabEventosProfile
              handleClickEventosAtivos={this.handleClickEventosAtivos}
              handleClickEventosAnteriores={this.handleClickEventosAnteriores}
            />
            {inscricoes.map(inscricao => (
              <EventoItem
                key={inscricao.id}
                inscricaoId={inscricao.id}
                evento={inscricao.evento}
                cargaHoraria={inscricao.cargaHoraria}
                pago={inscricao.pago}
                token={Token}
                atual={this.verifyDate(inscricao.evento.dataFim)}
                showEventosAtuais={showEventosAtuais}
                handleClickAtividades={this.handleClickAtividades}
              />
            ))}
          </div>
        </Fragment>
      );
    }
  }
}

const styleLoading = {
  position: "absolute",
  top: "50%",
  left: "50%",
  marginTop: "-50px",
  marginLeft: "-50px",
  width: "100px",
  height: "100px"
};
export default showEventos;
