import { connect } from 'react-redux';
import { change, submit, isSubmitting } from 'redux-form';
import React, { Component, Fragment } from 'react';
import {
  Button,
  SimpleForm,
  SaveButton,
  SelectInput,
  required,
  withDataProvider,
  GET_MANY_REFERENCE
} from 'react-admin';
import QrReader from 'react-qr-reader';
import IconContentAdd from '@material-ui/icons/CameraAlt';
import IconCancel from '@material-ui/icons/Cancel';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import PropTypes from 'prop-types';
import DialogContent from '@material-ui/core/DialogContent';
import DialogActions from '@material-ui/core/DialogActions';

import axios from 'axios';
import Swal from 'sweetalert2';
import PathName from '../../pathConst';

class PostQuickCreateButton extends Component {
  constructor(props) {
    super(props);
    this.state = {
      delay: 100,
      result: 'No result',
      error: '',
      days: {},
      dateId: null,
      showQRcode: false,
      showDates: false
    };

    this.handleScan = this.handleScan.bind(this);
  }
  componentWillMount = async () => {
    const { dataProvider } = this.props;
    await dataProvider(
      GET_MANY_REFERENCE,
      'Dias',
      {
        pagination: { page: 1, perPage: 0 },
        sort: { field: 'id', order: 'DESC' },
        target: 'turmaId',
        id: this.props.turmId
      },
      {
        onError: {
          notification: {
            body: 'Error: algo deu errado!',
            level: 'warning'
          }
        }
      }
    ).then(dias => {
      dias.data.forEach(element => {
        element.dia = new Date(element.dia).toLocaleDateString('pt-BR', {
          weekday: 'long',
          year: 'numeric',
          month: 'long',
          day: 'numeric',
          hour: 'numeric',
          minute: 'numeric'
        });
      });
      this.setState({ days: dias.data });
    });
  };
  handleClick = () => {
    this.setState({ showDates: true });
  };

  handleCloseClick = () => {
    this.setState({ showDates: false });
    this.setState({ showQRCode: false });
  };

  handleSaveClick = () => {
    const { submit } = this.props;

    // Trigger a submit of our custom quick create form
    // This is needed because our modal action buttons are oustide the form
    submit('post-quick-create');
  };

  handleSubmit = values => {
    this.setState({ diaId: values.dia });
    this.setState({ showQRCode: true });
    this.setState({ showDates: false });
  };
  handleScan(data) {
    console.log(this.state.diaId);
    console.log(data);
    console.log(localStorage.getItem('user_id'));
    let query = {
      where: { turmaId: this.props.turmId, inscritoId: data },
      fields: { id: true }
    };
    axios
      .get(
        `http://${PathName}/pet/api/Inscricoes/?filter=${JSON.stringify(
          query
        )}`,
        {
          headers: {
            Authorization: localStorage.getItem('access_token')
          }
        }
      )
      .then(data =>
        axios
          .post(
            `http://${PathName}/pet/api/Presencas`,
            {
              diaId: this.state.diaId,
              inscricaoId: data,
              responsavelPresencaId: localStorage.getItem('user_id')
            },
            {
              headers: {
                Authorization: localStorage.getItem('access_token')
              }
            }
          )
          .then(res => {
            Swal.fire({
              title: 'Operação realizada com sucesso!',
              type: 'success',
              timer: 3000,
              onBeforeOpen: () => {
                this.setState({ showQRCode: false });
              },
              onClose: () => {
                this.setState({ showQRCode: true });
              }
            });
          })
          .catch(err => {
            Swal.fire({
              type: 'error',
              title: 'Ops! algo deu errado',
              text: err.response.data.error.message,
              timer: 3000,
              onBeforeOpen: () => {
                this.setState({ showQRCode: false });
              },
              onClose: () => {
                this.setState({ showQRCode: true });
              }
            });
          })
      )
      .catch(err => {
        Swal.fire({
          type: 'error',
          title: 'Ops! algo deu errado',
          text: err.response.data.error.message,
          timer: 3000,
          onBeforeOpen: () => {
            this.setState({ showQRCode: false });
          },
          onClose: () => {
            this.setState({ showQRCode: true });
          }
        });
      });
  }
  handleError(err) {
    console.error(err);
  }
  openImageDialog() {
    this.refs.qrReader1.openImageDialog();
  }
  render() {
    const { showQRCode, showDates } = this.state;
    const { isSubmitting } = this.props;
    const previewStyle = {
      height: 240,
      width: 320
    };

    return (
      <Fragment>
        <Button onClick={this.handleClick} label="Escanear QR-Code">
          <IconContentAdd />
        </Button>
        <Dialog
          fullWidth
          open={showQRCode}
          onClose={this.handleCloseClick}
          aria-label="Scanner"
        >
          <DialogTitle>Scan QR-Code</DialogTitle>
          <DialogContent>
            <QrReader
              ref="qrReader1"
              delay={false}
              previewStyle={previewStyle}
              onError={this.handleError}
              onScan={this.handleScan}
              legacyMode={true}
            />
          </DialogContent>
          <DialogActions>
            <SaveButton
              label="Selecionar image"
              value="Submit QR Code"
              saving={isSubmitting}
              onClick={this.openImageDialog.bind(this)}
            />
            <Button label="ra.action.cancel" onClick={this.handleCloseClick}>
              <IconCancel />
            </Button>
          </DialogActions>
        </Dialog>
        <Dialog
          fullWidth
          open={showDates}
          onClose={this.handleCloseClick}
          aria-label="Scanner"
        >
          <DialogTitle>Presença</DialogTitle>
          <DialogContent>
            <SimpleForm
              // We override the redux-form name to avoid collision with the react-admin main form
              form="post-quick-create"
              resource="Inscricoes"
              // We override the redux-form onSubmit prop to handle the submission ourselves
              onSubmit={this.handleSubmit}
              // We want no toolbar at all as we have our modal actions
              toolbar={null}
            >
              <SelectInput
                label="Dia"
                source="dia"
                validate={required()}
                optionText="dia"
                optionValue="id"
                choices={this.state.days}
              />
            </SimpleForm>
          </DialogContent>
          <DialogActions>
            <SaveButton
              label="Scannear usuário"
              saving={isSubmitting}
              onClick={this.handleSaveClick}
            />
            <Button label="ra.action.cancel" onClick={this.handleCloseClick}>
              <IconCancel />
            </Button>
          </DialogActions>
        </Dialog>
      </Fragment>
    );
  }
}

PostQuickCreateButton.propTypes = {
  dataProvider: PropTypes.func.isRequired,
  dispatch: PropTypes.func.isRequired,
  record: PropTypes.object
};
const mapStateToProps = state => ({
  isSubmitting: isSubmitting('post-quick-create')(state)
});

const mapDispatchToProps = {
  change,

  submit
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withDataProvider(PostQuickCreateButton));
