import React, { Component } from 'react';
import Swal from 'sweetalert2';
import Axios from 'axios';
import PathName from '../../pathConst';
import formatDate from '../../Eventos/formatDate';
import '../userProfile.css';
import dateFormat from '../../Blog/dateBrFormat';
class atividesEventoItem extends Component {
  getTime = time => {
    return ' às ' + time.substring(11, 16) + 'h';
  };
  verifyDateActivity = () => {
    const { inscricao } = this.props;
    let dataAtividade = inscricao.turma.dias.slice(-1).pop(); // pega o ultimo dia
    dataAtividade = new Date(dataAtividade.dia);
    const dataAtual = new Date();
    return dataAtividade < dataAtual ? true : false;
  };

  verifyPresenca = (presencas, dia) => {
    return presencas.some(presenca => presenca.diaId === dia.id);
  };
  handleClick = () => {
    const { inscricao, token } = this.props;
    Swal.fire({
      title: `Deseja mesmo se desinscrever da atividade ${inscricao.atividade.nome}?`,
      type: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Confirmar',
      cancelButtonText: 'Cancelar'
    }).then(result => {
      if (result.value) {
        Axios.delete(
          `http://${PathName}/pet/api/Inscricoes/${inscricao.id}`,
          token
        )
          .then(res => {
            return Swal.fire({
              type: 'success',
              title: `Você se desinscrver da atividade ${inscricao.atividade.nome} com sucesso`,
              showConfirmButton: false,
              timer: 1500
            }).then(() => {
              window.location.reload();
            });
          })
          .catch(err => {
            return Swal.fire({
              type: 'error',
              title: 'Ops! algo deu errado',
              text: err.response.data.error.message
            });
          });
      }
    });
  };
  render() {
    const { inscricao } = this.props;
    return (
      <div className="card">
        <div className="card-content">
          <h1
            style={{ textAlign: 'center', textDecoration: 'underline' }}
            className="card-title"
          >
            {inscricao.atividade.nome}
          </h1>
          <div className="basicInfo grey-text text-darken-2">
            <p>
              <b>Local:</b>
              <span className="right">{inscricao.turma.nome}</span>
            </p>

            <ul className="collection with-header">
              <li className="collection-header">
                <h6>
                  {inscricao.turma.dias.length > 1 ? 'Datas' : 'Data'}{' '}
                  <i className="material-icons left">today</i>
                </h6>
              </li>
              {inscricao.turma.dias.map((dia, i) => (
                <li key={dia.id} className="collection-item">
                  <b style={{ marginLeft: '0.5em' }}>{`${i + 1}º dia:`}</b>
                  <span className="right">
                    {formatDate(dia.dia) + this.getTime(dia.dia)}
                  </span>
                </li>
              ))}
            </ul>
            <hr className="style-six" />

            <ul className="collection with-header">
              <li className="collection-header">
                <h6>
                  {inscricao.turma.dias.length > 1 ? 'Presenças' : 'Presença'}{' '}
                  <i className="material-icons left">group_add</i>
                </h6>
              </li>
              {inscricao.turma.dias.map((dia, i) => (
                <li key={dia.id} className="collection-item">
                  <b style={{ marginLeft: '0.5em' }}>{`${i + 1}º dia:`} </b>
                  {this.verifyPresenca(inscricao.presencas, dia) ? (
                    <i className="material-icons right">check</i>
                  ) : (
                    <i className="material-icons right">close</i>
                  )}
                  <span className="right" />
                </li>
              ))}
            </ul>
          </div>
        </div>
        {this.verifyDateActivity() ? null : (
          <div className="card-action">
            <button
              type="button"
              className="btn small red "
              onClick={this.handleClick}
            >
              Cancelar inscrição
            </button>
          </div>
        )}
      </div>
    );
  }
}

export default atividesEventoItem;
