import React, { Component, Fragment } from "react";
import "../userProfile.css";
import Axios from "axios";
import PathName from "../../pathConst";
import AtividadesEventoItem from "./atividadesEventoItem";
import CardError from "../../cardError";
import Header from "../../Header/Header";
import { Link } from "react-router-dom";

const borderStyle = {
  width: "100%",
  letterSpacing: "1px",
  color: "#1e88e5",
  border: "1px solid #bdbdbd",
  borderRadius: "5px"
};

class atividadesEvento extends Component {
  componentWillMount() {
    const { idEvento } = this.props;
    const usuarioId = localStorage.getItem("user_id");
    const token = localStorage.getItem("access_token");
    const Token = {
      headers: {
        Authorization: token
      }
    };
    Axios.get(
      `http://${PathName}/pet/api/Usuarios/${usuarioId}/eventos/${idEvento}`,
      Token
    ).then(res => {
      this.setState({
        evento: res.data.evento,
        inscricoes: res.data.inscricoes,
        Token
      });
    });
  }

  state = {
    evento: [],
    inscricoes: [],
    Token: {}
  };
  render() {
    const { evento, inscricoes, Token } = this.state;
    if (inscricoes.length === 0) {
      return (
        <Fragment>
          <Header />
          <CardError
            titulo={"Não consegui encontrar nenhuma atividade"}
            mensagem={
              "Aqui você encontraria todas as atividades que participou ou está partcipando. Porém, parece que você não se inscreveu em nenhuma até agora. "
            }
          />
        </Fragment>
      );
    } else {
      return (
        <Fragment>
          <Header />
          <div className="container" style={{ marginBottom: "1rem" }}>
            <ul className="tabs">
              <li className="tab hoverable" style={borderStyle}>
                <span>{`Atividades  ${evento.nome}`}</span>
              </li>
            </ul>
            {inscricoes.map((inscricao, i) => (
              <AtividadesEventoItem
                key={i}
                inscricao={inscricao}
                token={Token}
              />
            ))}
            <Link
              to="/pet/meusEventos"
              type="button"
              className="btn small blue lighten-1 "
            >
              Voltar
            </Link>
          </div>
        </Fragment>
      );
    }
  }
}

export default atividadesEvento;
