import React, { Fragment } from 'react';

import {
  Edit,
  ShowButton,
  ExportButton,
  EditController,
  ReferenceField,
  List,
  CardActions,
  BooleanInput,
  TextInput,
  TabbedForm,
  FormTab,
  BooleanField,
  NumberField,
  Link,
  SaveButton,
  Toolbar,
  required,
  LongTextInput,
  ReferenceManyField,
  Datagrid,
  EditButton,
  TextField
} from 'react-admin';
import AproveButton from './aproveButton';
import Swal from 'sweetalert2';
import Br from 'date-fns/locale/pt-BR';
import { DateTimeInput } from 'react-admin-date-inputs';
import DateFnsUtils from '@date-io/date-fns';
import IconContentAdd from '@material-ui/icons/Add';
import DeleteButtonWithConfirmation from '../deleteButtonWithConfirmation';
import dataProvider from '../dataProvider';
import { unparse as convertToCSV } from 'papaparse/papaparse.min';
import BackBotton from '../backButton';
import AddCategoria from './addDadoAdicional';
import AddValor from './addValor';
import Pagamento from '../statusPagamentoButton';
import RejectButton from './rejectButton';
import Button from '@material-ui/core/Button';

import Clipboard from 'react-clipboard.js';
DateFnsUtils.prototype.getStartOfMonth = DateFnsUtils.prototype.startOfMonth;

const ValoresActions = props => (
  <CardActions>
    <AddValor {...props} />
  </CardActions>
);
const DadosActions = props => (
  <CardActions>
    <AddCategoria {...props} />
  </CardActions>
);

const Ajudantes = props => (
  <CardActions {...props}>
    <Button
      component={Link}
      {...props}
      to={{ pathname: '/Ajudantes/create', state: props.eventId }}
      label="Adicionar ajudante"
    >
      <IconContentAdd /> Ajudante
    </Button>
  </CardActions>
);
const Toast = Swal.mixin({
  toast: true,
  position: 'top-end',
  showConfirmButton: false,
  timer: 3000
});

const AceitandoAjudantes = props => (
  <CardActions {...props}>
    <Clipboard
      className="btn blue lighten-1"
      onSuccess={() => {
        Toast.fire({
          type: 'success',
          title: 'Copiado com sucesso'
        });
      }}
      data-clipboard-text={`http://${window.location.host}/pet/solicitacaoAjudante/${props.eventId}`}
    >
      Link de compartilhamento
    </Clipboard>
  </CardActions>
);
const PostCreateToolbar = props => (
  <Toolbar {...props}>
    <SaveButton redirect={false} />
    <BackBotton />
  </Toolbar>
);

export const PostCreate = ({ permissions, ...props }) => (
  <EditController {...props}>
    {({ resource, record, redirect, save, basePath, version }) => {
      return (
        <TabbedForm
          basePath={basePath}
          redirect={redirect}
          resource={resource}
          record={record}
          save={save}
          version={version}
          toolbar={<PostCreateToolbar />}
        >
          <FormTab label="Dados eventos">
            <TextInput
              label="Nome do evento"
              validate={required()}
              source="nome"
            />
            <LongTextInput
              validate={required()}
              label="Descrição"
              source="descricao"
            />
            <LongTextInput
              label="Email enviado na inscrição"
              source="mensagemPagamento"
            />
            <TextInput
              label="Link de pagamento"
              source="linkPagamento"
            ></TextInput>
            <DateTimeInput
              label="Data início "
              source="dataInicio"
              providerOptions={{ utils: DateFnsUtils, locale: Br }}
            />
            <DateTimeInput
              providerOptions={{ utils: DateFnsUtils, locale: Br }}
              label="Data término "
              source="dataFim"
            />
            <DateTimeInput
              label="Início inscrições"
              source="inicioInscricoes"
              providerOptions={{ utils: DateFnsUtils, locale: Br }}
            />
            <DateTimeInput
              label=" Término inscrições"
              source="fimInscricoes"
              providerOptions={{ utils: DateFnsUtils, locale: Br }}
            />

            <BooleanInput label="Gratuito" source="gratuito"></BooleanInput>
            <BooleanInput
              label="Possui dados adicionais?"
              source="hasDadoAdicional"
            ></BooleanInput>
            <BooleanInput
              label="Recrutando ajudante"
              source="aceitandoAjudante"
            ></BooleanInput>
          </FormTab>
          {record && !record.gratuito && (
            <FormTab label="Valores">
              <Fragment>
                <Edit actions={null} title="/" {...props}>
                  <ReferenceManyField
                    {...props}
                    basePath={basePath}
                    resource={resource}
                    record={record}
                    reference="Valores"
                    target="eventoId"
                    title={false}
                  >
                    <List
                      actions={<ValoresActions {...{ eventId: props.id }} />}
                      title="Valores"
                      bulkActionButtons={false}
                      filter={{ eventoId: props.id }}
                      {...props}
                    >
                      <Datagrid>
                        <TextField source="nome" label="Categoria"></TextField>
                        <NumberField
                          options={{ style: 'currency', currency: 'BRL' }}
                          source="preco"
                        />
                        <EditButton label="Gerenciar dados"></EditButton>
                        {permissions === 'admin' && (
                          <DeleteButtonWithConfirmation
                            redirect={false}
                            label="Remover valor"
                            undoable={true}
                          />
                        )}
                      </Datagrid>
                    </List>
                  </ReferenceManyField>
                </Edit>
              </Fragment>
            </FormTab>
          )}

          {record && record.hasDadoAdicional && (
            <FormTab label="Dados adicionais">
              <Fragment>
                <Edit actions={null} title="/" {...props}>
                  <ReferenceManyField
                    {...props}
                    basePath={basePath}
                    resource={resource}
                    record={record}
                    reference="DadosAdicionais"
                    target="eventosId"
                    title={false}
                  >
                    <List
                      actions={<DadosActions {...{ eventId: props.id }} />}
                      title="Dados adicionais"
                      bulkActionButtons={false}
                      filter={{ eventoId: props.id }}
                      {...props}
                    >
                      <Datagrid>
                        <TextField source="nome" />
                        <BooleanField
                          label="Possui opções?"
                          source="respIsBoolean"
                        />
                        <EditButton label="Gerenicar Dados" />
                        {permissions === 'admin' && (
                          <DeleteButtonWithConfirmation
                            redirect={false}
                            label="Remover dado adicional"
                            undoable={true}
                          />
                        )}
                      </Datagrid>
                    </List>
                  </ReferenceManyField>
                </Edit>
              </Fragment>
            </FormTab>
          )}
          <FormTab label="Ajudantes">
            <Fragment>
              <Edit actions={null} title="/" {...props}>
                <ReferenceManyField
                  {...props}
                  basePath={basePath}
                  resource={resource}
                  record={record}
                  reference="Ajudantes"
                  target="eventoId"
                  title={false}
                >
                  <List
                    actions={<Ajudantes {...{ eventId: props.id }}></Ajudantes>}
                    title="Ajudantes"
                    bulkActionButtons={false}
                    filter={{ eventoId: props.id }}
                    {...props}
                  >
                    <Datagrid>
                      <ReferenceField
                        linkType={false}
                        label="Nome"
                        reference="Usuarios"
                        source="usuarioId"
                      >
                        <TextField source="nome" />
                      </ReferenceField>
                      <ReferenceField
                        linkType={false}
                        label="CPF"
                        reference="Usuarios"
                        source="usuarioId"
                      >
                        <TextField source="cpf" />
                      </ReferenceField>
                      <ReferenceField
                        label="E-mail"
                        linkType={false}
                        reference="Usuarios"
                        source="usuarioId"
                      >
                        <TextField source="email" />
                      </ReferenceField>
                      {permissions === 'admin' && (
                        <DeleteButtonWithConfirmation
                          redirect={false}
                          label="Remover ajudante"
                          undoable={true}
                        />
                      )}
                    </Datagrid>
                  </List>
                </ReferenceManyField>
              </Edit>
            </Fragment>
          </FormTab>
          {record && record.aceitandoAjudante && (
            <FormTab label="Solicitação p/ ajudante">
              <Fragment>
                <Edit actions={null} title="/" {...props}>
                  <ReferenceManyField
                    {...props}
                    basePath={basePath}
                    resource={resource}
                    record={record}
                    reference="Solicitacoes"
                    target="eventoId"
                    title={false}
                  >
                    <List
                      actions={
                        <AceitandoAjudantes
                          {...{ eventId: props.id }}
                        ></AceitandoAjudantes>
                      }
                      title="Solicitacao"
                      bulkActionButtons={false}
                      filter={{ eventoId: props.id }}
                      {...props}
                    >
                      <Datagrid>
                        <ReferenceField
                          linkType={false}
                          label="Nome"
                          reference="Usuarios"
                          source="usuarioId"
                        >
                          <TextField source="nome" />
                        </ReferenceField>
                        <ReferenceField
                          linkType={false}
                          label="CPF"
                          reference="Usuarios"
                          source="usuarioId"
                        >
                          <TextField source="cpf" />
                        </ReferenceField>
                        <ReferenceField
                          label="E-mail"
                          linkType={false}
                          reference="Usuarios"
                          source="usuarioId"
                        >
                          <TextField source="email" />
                        </ReferenceField>
                        <AproveButton
                          label="Aceitar"
                          {...{ eventId: props.id }}
                        ></AproveButton>
                        <RejectButton
                          redirect={false}
                          label="Rejeitar"
                          undoable={true}
                        />
                      </Datagrid>
                    </List>
                  </ReferenceManyField>
                </Edit>
              </Fragment>
            </FormTab>
          )}
        </TabbedForm>
      );
    }}
  </EditController>
);

export default PostCreate;
