import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withDataProvider, SaveButton, CREATE } from 'react-admin';

// A custom action creator which modifies the values before calling the default crudCreate action creator
const saveWithNote = (values, dataProvider) => {};
class SaveWithNoteButtonView extends Component {
  handleClick = () => {
    const { handleSubmit, dataProvider } = this.props;

    return handleSubmit(values => {
      dataProvider(
        CREATE,
        'Ajudantes/array',
        {
          pagination: { page: 1, perPage: 0 },
          sort: { field: 'id', order: 'DESC' },
          data: { data: values.usuarioId, eventoId: parseInt(values.eventoId) }
        },
        {
          onSuccess: {
            unselectAll: true,
            refresh: true,
            redirectTo: `/Eventos/${values.eventoId}/4`
          },
          onError: {
            notification: {
              body: 'Error: algo deu errado!',
              level: 'warning'
            }
          }
        }
      ).then(res => {
        console.log(res);
      });
    });
  };

  render() {
    const { handleSubmitWithRedirect, saveWithNote, ...props } = this.props;

    return (
      <SaveButton handleSubmitWithRedirect={this.handleClick} {...props} />
    );
  }
}

export default connect(
  undefined,
  { saveWithNote }
)(withDataProvider(SaveWithNoteButtonView));
