import React, { Component, Fragment } from 'react';
import { Select } from 'react-materialize';

class dadoAdicionalSingleBool extends Component {
  getValue = () => {
    const { opcaoSelecionada, dadoAdicionalId } = this.props;
    return opcaoSelecionada.filter(o => o.dadoAdicionalId === dadoAdicionalId);
  };
  render() {
    const { nome, desc, opcoes } = this.props;
    return (
      <Fragment>
        <h5>
          <i className="material-icons left">question_answer</i>
          {nome}
        </h5>
        <p className="basicInfo grey-text text-darken-2">{desc}</p>
        <Select
          s={12}
          value={this.getValue()[0].idOpcao}
          onChange={this.props.handleChangeSingleOption}
          name="opcaoSelecionada"
        >
          <option value="" disabled defaultValue={this.getValue()[0].idOpcao}>
            Escolha apenas uma opção
          </option>
          {opcoes.map(opcao => (
            <option key={opcao.id} value={opcao.id}>
              {opcao.valor}
            </option>
          ))}
        </Select>
        <br />
      </Fragment>
    );
  }
}

export default dadoAdicionalSingleBool;
