import React, { Component, Fragment } from "react";
import formatDate from "./formatDate";
import moment from "moment";
// eslint-disable-next-line no-unused-vars
import tz from "moment-timezone";
import { Link } from "react-router-dom";
import { Row, Col } from "react-materialize";
import "../Usuario/userProfile.css";
import "./centerIfSmall.css";
import fotoLambda from "../../images/lambda.png";
class sobreEvento extends Component {
  componentWillMount() {
    const userId = localStorage.getItem("user_id");
    if (userId === null) {
      this.setState({ userLogged: false });
    } else {
      this.setState({ userLogged: true });
    }
  }
  state = {
    userLogged: false
  };

  getStyleButton = () => {
    const { userLogged } = this.state;
    return userLogged
      ? "btn blue lighten-1 right"
      : "disabled btn blue lighten-1 right";
  };

  getTextButton = () => {
    const { userLogged } = this.state;
    return userLogged ? "Inscrever-se" : "Efetue login";
  };

  formatValor = valor => {
    return valor === 0 ? "Gratuito" : `R$ ${valor}`;
  };

  getTime = time => {
    return time.substring(11, 16);
  };

  verifyDate = () => {
    let { dataFim } = this.props;
    let dataAtual = new Date();
    dataAtual.setHours(0, 0, 0, 0);
    dataFim = Date.parse(dataFim);
    return dataFim.valueOf() >= dataAtual.valueOf();
  };

  buildRow = (key, value) => (
    <Row>
      <Col s={12} m={6}>
        <b className="left hide-on-small-and-down">{key}</b>
        <b className="really-center-span hide-on-med-and-up">{key}</b>
      </Col>

      <Col s={12} m={6}>
        <span className="right hide-on-small-and-down">{value}</span>
        <span className="really-center-span hide-on-med-and-up">{value}</span>
      </Col>
    </Row>
  );

  render() {
    const {
      nomeEvento,
      descricao,
      dataInicio,
      dataFim,
      idEvento,
      foto,
      valoresEvento,
      inicioInscricoes,
      fimInscricoes
    } = this.props;
    const titleStyle = {
      fontWeight: "bold",
      fontSize: `${2}rem`,
      fontFamily: "Nunito"
    };
    return (
      <div className="container">
        <div className="card-panel z-depth-2">
          <div className="row">
            <div id="sobre" className="col s12">
              <h3 style={titleStyle} className="center">
                {nomeEvento}
              </h3>
              <div>
                <img
                  src={foto ? foto : fotoLambda}
                  className="center"
                  alt=""
                  style={{
                    display: "inline-block",
                    boxSizing: "border-box",
                    outline: 0,
                    borderRadius: 100,
                    height: 100,
                    width: 100
                  }}
                />
              </div>
              <h5>
                <i className="material-icons left">equalizer</i>Sobre o evento
              </h5>
              <p className="basicInfo grey-text text-darken-2">{descricao}</p>
              <br />
              <h5>
                <i className="material-icons left">info</i>Informações
                importantes
              </h5>
              <div className="basicInfo grey-text text-darken-2">
                <hr className="style-six" />
                {this.buildRow(
                  "Inicio do evento:",
                  formatDate(
                    moment.tz(dataInicio, "America/Sao_Paulo").format()
                  )
                )}
                <hr className="style-six" />
                {this.buildRow(
                  "Término do evento:",
                  formatDate(moment.tz(dataFim, "America/Sao_Paulo").format())
                )}
                {inicioInscricoes ? (
                  <Fragment>
                    <hr className="style-six" />
                    {this.buildRow(
                      "Início das inscrições:",
                      formatDate(
                        moment
                          .tz(inicioInscricoes, "America/Sao_Paulo")
                          .format()
                      )
                    )}
                  </Fragment>
                ) : null}
                {fimInscricoes ? (
                  <Fragment>
                    <hr className="style-six" />
                    {this.buildRow(
                      "Término das inscrições:",
                      formatDate(
                        moment.tz(fimInscricoes, "America/Sao_Paulo").format()
                      )
                    )}
                  </Fragment>
                ) : null}
                {valoresEvento.map(valor => (
                  <Fragment key={valor.id}>
                    <hr className="style-six" />
                    {this.buildRow(valor.nome, this.formatValor(valor.preco))}
                  </Fragment>
                ))}
              </div>
              <br />
              <Link to="/pet/eventos" className="btn blue lighten-1 left">
                Voltar
              </Link>
              {this.verifyDate() ? (
                <Link
                  to={`/pet/eventos/${idEvento}/cadastro`}
                  className={this.getStyleButton()}
                  style={{
                    textOverflow: "ellipsis",
                    whiteSpace: "nowrap",
                    overflow: "hidden"
                  }}
                >
                  {this.getTextButton()}
                </Link>
              ) : null}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default sobreEvento;
