import React from 'react';
import {
  ShowButton,
  List,
  CardActions,
  Show,
  ReferenceManyField,
  Datagrid,
  TextField,
  EditButton
} from 'react-admin';
import Turma from './addTurma';
import BackButton from '../backButton';
import DeleteButtonWithConfirmation from '../deleteButtonWithConfirmation';
const PostActions = props => (
  <CardActions>
    <BackButton />
    {
      (props.permissions === 'Petiano' || props.permissions === 'admin') && (
        <Turma {...props} />
      )}
  </CardActions>
);

export const PostCreate = ({ permissions, ...props }) => (
  <Show actions={null} title=" " {...props}>
    <ReferenceManyField label="" reference="Turmas" target="atividadeId">
      <List
        actions={
          <PostActions
            {...{
              atividadeId: props.id
            }}
            {...{ permissions: permissions }}
          />
        }
        filter={{ atividadeId: props.id }}
        {...props}
        bulkActionButtons={false}
      >
        <Datagrid>
          <TextField label="Local" source="nome" />
          <TextField
            label="Número de inscritos"
            source="quantidadeAtualInscritos"
          />

          <ShowButton label="Visualizar inscrições" />

          {(permissions === 'Petiano' || permissions === 'admin') && (
            <EditButton label="Gerenciar dados" />
          )}
          {permissions === 'admin' && (
            <DeleteButtonWithConfirmation
              redirect={false}
              label="Remover turma"
              undoable={true}
            />
          )}
        </Datagrid>
      </List>
    </ReferenceManyField>
  </Show>
);

export default PostCreate;
