import {
  AUTH_LOGOUT,
  AUTH_ERROR,
  AUTH_GET_PERMISSIONS,
  AUTH_CHECK
} from "react-admin";
import axios from "axios";
import PathName from "../pathConst";

export default (type, params) => {
  if (type === AUTH_LOGOUT) {
    return Promise.resolve();
  }
  if (type === AUTH_ERROR) {
    const { status } = params;
    if (status === 401 || status === 403) {
      localStorage.clear();
      return Promise.reject();
    }
  }

  if (type === AUTH_CHECK) {
    async function verifyPetiano() {
      const userId = await localStorage.getItem("user_id");
      const token = localStorage.getItem("access_token");
      const Token = {
        headers: {
          Authorization: token
        }
      };
      const filter = {
        fields: {
          id: true,
          realm: true
        }
      };
      let res = await axios.get(
        `${PathName}/pet/api/Usuarios/${userId}/?filter=${JSON.stringify(
          filter
        )}`,
        Token
      );
      return res.data.realm === "Petiano" ||
        res.data.realm === "Ajudante" ||
        res.data.realm === "admin"
        ? Promise.resolve()
        : Promise.reject();
    }
    return verifyPetiano();
  }
  if (type === AUTH_GET_PERMISSIONS) {
    async function getRealm() {
      const user_id = await localStorage.getItem("user_id");
      const filter = {
        fields: {
          realm: true
        }
      };
      const token = localStorage.getItem("access_token");
      const Token = {
        headers: {
          Authorization: token
        }
      };
      const role = await axios.get(
        `${PathName}/pet/api/Usuarios/${user_id}?filter=${JSON.stringify(
          filter
        )}`,
        Token
      );
      return Promise.resolve(role.data.realm);
    }
    return getRealm();
  }
  return Promise.reject("Unknown method");
};
