import React from "react";
import {
  Create,
  SimpleForm,
  required,
  TextInput,
  LongTextInput
} from "react-admin";
import RichTextInput from "ra-input-rich-text";

const validateUserCreation = values => {
  const errors = {};

  return errors;
};

export const PostCreate = props => (
  <Create title="Postagem/add" {...props}>
    <SimpleForm validate={validateUserCreation}>
      <TextInput validate={required()} label="Título" source="titulo" />
      <LongTextInput
        validate={required()}
        label="Descrição"
        source="descricao"
      />
      <RichTextInput
        validate={required()}
        toolbar={[
          ["bold", "italic", "underline", "strike"],
          [{ script: "super" }, { script: "sub" }],
          [{ header: "1" }, { header: "2" }, "blockquote", "code-block"],
          [
            { list: "ordered" },
            { list: "bullet" },
            { indent: "-1" },
            { indent: "+1" }
          ],
          ["direction", { align: [] }],
          ["link", "image"],
          ["clean"]
        ]}
        source="conteudo"
      />
    </SimpleForm>
  </Create>
);

export default PostCreate;
